void mainImage( out vec4 o, vec2 u )
{
    vec2 f = iResolution.xy;
    o = 0.*o + length(u = 9. * (u + u - f) / f.y) - 6.;

    for (float i, t = .1 * iTime; 
         i++ < 9.; 
         o += .5 * abs(f.x+f.y) * o.x) 
        u *= mat2(cos(t * cos(i) + i + vec4(0,11,33,0))),
        f = cos(u.yx - cos(f)),
        u += .5 * f + t; // <- gets faster and faster     

    o = exp(2. / exp(1e-3 * o * o * vec4(7,3,1,0)) - 2.);
}


