float noise(vec2 p) {
    return fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453);
}

float smoothNoise(vec2 p) {
    vec2 i = floor(p), f = fract(p);
    return mix(mix(noise(i), noise(i + vec2(1.0, 0.0)), f.x * f.x * (3.0 - 2.0 * f.x)),
               mix(noise(i + vec2(0.0, 1.0)), noise(i + vec2(1.0, 1.0)), f.x * f.x * (3.0 - 2.0 * f.x)),
               f.y * f.y * (3.0 - 2.0 * f.y));
}

float fbm(vec2 p) {
    float v = 0.0, a = 0.5;
    mat2 rot = mat2(1.0, 0.0, 0.0, 1.5);
    for (int i = 0; i < 5; i++, a *= 0.5, p = rot * p + vec2(0.0, 100.0))
        v += a * smoothNoise(p);
    return v;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord.xy / iResolution.xy - 0.5) * vec2(iResolution.x / iResolution.y, 1.0);
    float flame = smoothstep(0.2, 1.0, uv.y + fbm(uv * vec2(3.0, 6.0) + vec2(0.0, iTime * 0.5)) * 0.5);
    fragColor = vec4(mix(vec3(0.0), vec3(1.0), flame), 1.0);  // Inverted colors
}
