// Fork of "Colour Diamond Kaleidoscope" by ShadeWhizz. https://shadertoy.com/view/lfySDd
// 2024-05-17 03:24:21


vec2 mouse;
#define iTime  (recomputeTimeIntoSeeSawOfTimeframe(iTime)) 
#define TIME_FRAME_OF_BOUNCE 9.3

// floating point only version
float recomputeTimeIntoSeeSawOfTimeframe(float theTime) {
   float timeSlice = theTime/TIME_FRAME_OF_BOUNCE;
   float numIterations = floor( timeSlice );
   float posInSlice = fract(timeSlice);

   // is this a even or odd slice, evem slices go backwards..
   if( fract(numIterations/2.0) == 0.0) {
       // even slice, go forwards through slice
       return TIME_FRAME_OF_BOUNCE*posInSlice;
   } else {
       // backwards through Time Slice
       return TIME_FRAME_OF_BOUNCE*(1.0-posInSlice);
   }
}

#define M_PI 3.1415926535897932384626433832795
// base shape
float base(vec2 uv, float t) {
    vec2 ndc = (uv - 0.5) * 2.0;
    float h = 1.0 - dot(ndc,ndc);
    float gray = sin(t + h * 2.0*M_PI);
    return gray;
}

// fractaling the base shape
float map(vec2 uv, float t) {
    float themin = 1.0;
    for (int i = 1; i < 3; i++) {
        vec2 ndc = (uv - 0.5) * 2.0;
        vec2 uv2 = fract(float(i)*ndc)*mouse.y/mouse.x;
        // variant
        //vec2 uv2 = cos(1.0/float(i)*M_PI*ndc); // pow2?
        float h = base(uv2, t);
        themin = min(themin, h);
    }
    return themin;
}
vec3 image(vec2 uv, float t) {
    float z = map(uv,t); // map but change base given what base is
    float w = map(uv,t+0.5);
    float u = map(uv, t+1.0);
    return vec3(z,w,u);

}
vec2 ndc(vec2 uv) {
    return (uv - 0.5) * 2.0;
}

vec3 kaleidoscope(vec2 uv, float t) {
    vec2 ndc = ndc(uv);
    float theta = atan(ndc.y, ndc.x);
    float r = sqrt(dot(ndc,ndc));
    //theta = cos(3.0*cos(t*0.15)*theta);
    //theta = mod(2.0*cos(t*0.15)*theta, 1.0*M_PI);
    theta = mod(mod(t*0.15,1.0)*theta*mouse.x/mouse.y, 1.0*M_PI);
    //theta = cos(theta);
    uv = r * vec2(cos(theta*mouse.x/mouse.y),sin(theta*mouse.x/mouse.y));
    return image(uv, t);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(iMouse.xy == vec2(0.)) {
        mouse.x = cos(iTime/10.0);
        mouse.y = sin(iTime/23.14)*3.1415;
    } else {
      mouse = iMouse.xy/iResolution.xy;
    }

    vec2 uv = fragCoord/iResolution.xy;
    fragColor = vec4(kaleidoscope(uv, iTime * 1.0),1.0);
}

