//hash by IQ https://www.shadertoy.com/view/XlXcW4
const uint k = 1103515245U;
vec3 hash( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}

mat2 rot(float t){
    return mat2(cos(t), sin(t), -sin(t), cos(t));
}

float noise(vec2 p, int d, uvec3 x){
    p *= 0.005;
    float sum;
    vec2 dir;
    vec3 h;
    for (int i = 1; i<d+1; i++){
        h = hash(x + uvec3(i, i*i, 2<<i)) * 2. - 1.;
        sum  += (texture(iChannel3, p * rot(atan(h.y, h.x)) * float(i)).x*2.-1.)/float(i);
    }
    return sum;
}



void mainImage( out vec4 O, vec2 U ){
    vec2 r = iResolution.xy;
    vec2 uv = U/r;
    vec2 cuv = (2.*U-r)/r.y;

    O = vec4(0);
    float f = noise(cuv, 2<<8, uvec3(1,2,3));
    vec3 norm = vec3(dFdx(f), dFdy(f), 0);
    norm.z = sqrt(1. - norm.x*norm.x - norm.y*norm.y);

    vec3 light = normalize(vec3(sin(3.), cos(3.), 1.));

    O = mix(vec4(.8,.8,.5,1.), vec4(.2, .4,.3,1.), f) * dot(light, norm);

    if (f < 0.) O *= pow(vec4(.3, .4, .8, 1.), vec4(-f*5.));

    O *= 1.-smoothstep(-.8, 1.3, noise(cuv + iTime*.02 - light.xy*.2, 2<<1, uvec3(2,3,5)));

    O = mix(O, vec4(1), smoothstep(-.8, 1.3, noise(cuv + iTime*.02, 2<<5, uvec3(2,3,5))));

}
