const float
    CAM_DIST = 12.,
    CAM_FLE = 2.,
    BB_DIST = 12.,
    RAY_MIN_DIST = 0.1,
    RAY_MAX_DIST = CAM_DIST+1.1*BB_DIST;

const int MAX_MARCHING_STEPS = 600;
const float
    RAY_STEP_FACTOR = 0.039,
    RAY_PRECISION = 1.E-1;

// data from sdf
vec3 mat_to = vec3(0);
vec3 mat_to_id = vec3(0);
vec4 mat_tc = vec4(0);

float map(in vec3 p) {
    float rxy = 0., au = 0.0, av = 0.0;
    vec2 z = vec2(0.);
    float d = 1.E6;

    #if 1
    // helix
    float phase = PI2 * iTime / 15.;
    float lead   = mix(3., 12., 0.5 + 0.5 * sin(phase)); 
    float radius = mix(3., 4., 0.5 + 0.5 * cos(phase));


    #ifdef HELIX_HOLTSETIO
    vec3 helix = closestHelixHolseito(p, lead, radius);
    #else
    vec3 helix = closestHelix(p, lead, radius);
    #endif
    //float hx_d = length(p - helix) - .5;

    p = helixCoordinates(p, helix, lead, radius);
	//vec2 hx_uv = vec2(p.x, atan(p.y, p.z) / PI2);
    #endif

    p.zxy = p;

    #if 1
    // cylinder-julia
    z = p.xy; au = p.z;   
    z = cmul(z,z);

    av = atan(z.y, z.x);
    rxy = length(z);

    p = vec3(1.*vec2(4.,1.) * vec2(au, sqrt(3.)* av), log(rxy))/PI;
    #endif

    #if 1
    // regular hexagonal tiles 
    p.xy = hexgrid2hex(p.xy, mat_to_id);    
    p.xy = inversesc(p.xy, int(6.));

    // torus,sphere,2spheres       
    rxy = length(p.xy); au = atan(p.y, p.x);    
    z = 2.*ccos(PI*vec2(rxy,2.*p.z));
    z.x += 1.95;

    p = vec3(log(length(z)-0.1),vec2(au, atan(z.y,z.x))*0.5/PI);

    vec2 to_uv = p.yz;
    float to_d = log(length(z)+0.8);
    mat_to = vec3(to_uv,to_d);
    d = min(d,to_d);
    #endif    

    #if 1
    p = p.yzx ;    
    // truchet 3D box grid
    p *= vec3(18.,4.,1.);
    ivec3 l0 = -ivec3(18./2.,4./2.,0);
    ivec3 l1 = +ivec3(18./2.,4./2.,1.)-ivec3(1.);
    vec3 tc_id = floor(p);
    tc_id = clamp(tc_id, vec3(l0), vec3(l1));
    p -= tc_id +0.5;
    #else
    vec3 tc_id = vec3(1.);
    #endif

    // truchet 3D box tiles
    float rnd = hash21(tc_id.xy);

    if(rnd<.5) p.x = -p.x;
    mat_tc = vec4(p,rnd);

    float tc_d = -1.; float r1 = 0.125;
    if(fract(rnd*77.57+iTime/6.)>.75) {       
       p.xy = length(p.xy-.5)<length(p.xy+.5)
                ? vec2(p.xy-.5) 
                : vec2(p.xy+.5);
       tc_d = sdTorus(p.xzy, vec2(0.5,r1));
    } else {
       tc_d = opSmoothUnion( sdCylinder( p, r1 ), sdCylinder( p.yxz, r1 ), 0.05 );
    }    
    d = min(d,tc_d);

    // return
    return d;
}

// intersect
float intersect(in vec3 ro, in vec3 rd, in float px) {
    float res = -1.0;

    // bb
    vec2 dis = isphere( ro, rd , abs(BB_DIST));    
    if( dis.y<0.0 ) return -1.0;
    dis.x = max( dis.x, RAY_MIN_DIST );
    dis.y = min( dis.y, RAY_MAX_DIST );

    // raymarch signed distance field    
	float fh = RAY_STEP_FACTOR;
    float t = dis.x;
    for( int i=0; i<MAX_MARCHING_STEPS; i++  ) {
        vec3 pos = ro + rd*t;
        float th = RAY_PRECISION * px * t;
        float h = map( pos);
        if( t>dis.y || h<th ) break;
        t += h*fh;
    }
    if( t<dis.y ) {
        res = t;
    }
    return res;
}

vec3 calcNormal( in vec3 p, in float prec ) {
    vec2 e = vec2( prec, 0. );
    return normalize(vec3(
		map(p+e.xyy) - map(p-e.xyy),
		map(p+e.yxy) - map(p-e.yxy),
		map(p+e.yyx) - map(p-e.yyx) )
    	);
}

vec3 transform(in vec3 p, in float time) { 
	if (iMouse.x > 0.0) {
        vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
		p.yz = crot(p.yz,-PI*m.y);
		p.zx = crot(p.zx,-PI*m.x);
	}
    p.yz = crot(p.yz,-1.*PI*(time+10.));
    p.zx = crot(p.zx,-2.*PI*(time+15.));
	return p.xyz;
}

// textures
float grid_uv(in vec2 uv) {
    // uv grid alpha mask
    vec2 sw = abs(fract(uv-0.25)-0.5)-0.25;
    float sdf = min(sw.x,sw.y)*-max(sw.x,sw.y);
    float pp = fwidth(sdf);
    return smoothstep(+pp,-pp,sdf);
}

vec4 cells(in vec2 uv, in float h) {
    // sectors, uv-grid
    vec4 C;
    C.a = 1.;
    C.rgb = h2rgb(h);
    C.rgb *= (0.5+ 0.5 * grid_uv(uv))*0.7;
    return C;
}

vec4 ribbon(in float uv_x, in float time) {
    float sdf = -(2.*pulsecollision(1.,1./3. * uv_x, time, 2./6.)-1.);
    float px = fwidth(sdf); float a = smoothstep(px+0.1,-px-0.1,sdf+0.2);    
    return vec4( h2rgb(sdf -2./6.),a );
}

vec4 truchet3D(in vec3 p, in float rnd ,in float time) {
    float sdf = sdBox( p, vec3(0.5*0.9));
    float px = fwidth(sdf); float a = smoothstep(px,-px,sdf);
    return mix(vec4(1.), vec4(h2rgb( fract(abs(rnd)*22.93 + time)),1.), a);
}

// main
void mainImage(out vec4 O, in vec2 fragCoord) {
    // normalized time
    float ntime = iTime/60.;

    // anti-aliasing to take a picture
    vec3 tot = vec3(0.0);
    #define AA 1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ ) {       
        // normalized coordinates viewport
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 uv = (2.0*(fragCoord+o) - iResolution.xy)/iResolution.y;

        // camera viewport --> worldspace
        vec3 ro = vec3(0,0,-CAM_DIST);
        vec3 rd = vec3(-uv.x,uv.y,CAM_FLE);
             ro = transform(ro,ntime);
             rd = normalize(transform(rd,ntime));

        // distance hit ray surface
        float px = 2.0/( iResolution.y*CAM_FLE );
        float t = intersect( ro, rd, px );

        // background
        vec3 sky_col = mix(vec3(0.714,0.145,0.533), vec3(0.000,0.482,1.000), rd.y*.5+.5);
        //vec3 sky_col = vec3(0);
        vec4 C = vec4(sky_col,1.);
        O = C;

        // object surface
        if (t >= 0.) {
            // surface properties
            vec3 p = ro + rd * t;
            vec3 n = calcNormal( p, 0.001);

            const float r0 = 0.5;
            float a_rpt_tc = 0.5+0.5*sin(PI2*ntime);

            // draw textures
            O = vec4(0);

            if(O.a<1.) {          
                float hue = (mat_to_id.x + 2.)/4. + (mat_to_id.z )/8.;
                C = cells(mat_to.xy/2., hue);
                C.a = mat_to.z>r0 ? 1.-a_rpt_tc : O.a;
                O += (1.-O.a) * vec4( C.rgb, 1 ) * C.a;
            }

            if(O.a<1.) {
                C =  mat_to.z<r0 ? ribbon( 6.*3.* mat_to.x,  6.*ntime) : vec4(0.);
                C.rgb *= 0.7;
                O += (1.-O.a) * vec4( C.rgb, 1 ) * C.a;
            }

            if(O.a<1.) {
                C = truchet3D(mat_tc.xyz, mat_tc.w, ntime);
                C.a = a_rpt_tc; C.rgb *= 0.5;
                O += (1.-O.a) * vec4( C.rgb, 1 ) * C.a;
            }

            C = O;

            // lighting
            vec3  came_li_o   = transform(vec3(-10.,10.,-10.),ntime); // origine
            vec3  came_li_dir = normalize(p - came_li_o);             // direction
            vec3  came_li_col = vec3(1.,1.,.5);                       // color
            float came_li_shi = 10.;                                  // shininess            

            float cosa = 1.;
            O.rgb += 0.1 * cosa * C.rgb * sky_col;           // amb
            cosa = clamp(dot(-came_li_dir, n), 0., 1.);
            O.rgb += 0.5 * cosa * C.rgb * came_li_col;       // dif       
            cosa = pow(clamp(dot(reflect(came_li_dir, n), -rd), 0., 1.), came_li_shi);           
            O.rgb += 0.4 *(cosa>0.? cosa:0.) * came_li_col ; // spec

            // fog
            O.rgb = mix(O.rgb, sky_col, 1.0 - exp(-0.4E-3 * pow(t,3.)));    
        }
        tot += O.rgb;
    }
    tot /= float(AA*AA);

    // gamma correction
    O = vec4(rgb2srgb(tot),1.);
}

#define HELIX_HOLTSETIO

// const
const float
    PI = abs(atan(0.,-1.)),
    PI_2 = PI/2.,
    PI2 = PI*2.;   

// 1D transformations
float pulsecollision(in float pf, in float x, in float x0, in float pw) {
    float ntriw_p = abs( fract( pf*(x - x0) )-0.5);
    float ntriw_n = abs( fract( pf*(x + x0) )-0.5);
    float pp = smoothstep( 0.5*pw, 0., ntriw_p );
    float pn = smoothstep( 0.5*pw, 0., ntriw_n );
    return (pp+pn);
}

float hash21(in vec2 p) {
    return fract(sin(dot(p, vec2(27.609, 57.583)))*43758.5453);
}

// complex
vec2 expi(in float x) {
  return vec2(cos(x),sin(x));
}

vec2 cmul(in vec2 za, in vec2 zb) {
    return za*mat2(zb.x,-zb.y,zb.yx);
}

vec2 crot(in vec2 z, in float a) {
    return cmul(expi(a),z);
}

vec2 cinv(in vec2 z) {
    return z*vec2(1,-1)/dot(z,z);
}

vec2 cdiv(in vec2 z, in vec2 w) {
    return cmul(z,cinv(w));
}

vec2 cpow(in vec2 z, in int n) {
  float r = length(z);
  float theta = atan(z.y,z.x);
  return pow(r,float(n))*normalize(vec2(cos(float(n)*theta),sin(float(n)*theta)));
}

vec2 csqr(in vec2 z) {
    return vec2(z.x * z.x - z.y * z.y, 2. * z.x * z.y );
}

vec2 ccos(in vec2 z) {
    vec2 d=vec2(exp(z.y),exp(-z.y));
	return vec2(cos(z.x)*(d.x+d.y)*.5,-sin(z.x)*(d.x-d.y)*.5);
}

// https://www.shadertoy.com/view/tsfyRj by mla
// "Schwarz-Christoffel Mapping"
float binomial(in float a, in int n) {
   float s = 1.0;
   for (int i = n; i >= 1; i--,a--) {
     s *= float(a)/float(i);
   }
   return s;
}

float gamma(in float z) {
  const float[8] p = float[](
    676.5203681218851,
    -1259.1392167224028,
    771.32342877765313,
    -176.61502916214059,
    12.507343278686905,
    -0.13857109526572012,
    9.9843695780195716e-6,
    1.5056327351493116e-7
  );
  z -= 1.0;
  float x = 0.99999999999980993;
  for (int i = 0; i < 8; i++) {
    float pval = p[i];
    x += pval/(z+float(i+1));
  }
  float t = z + 8.0 - 0.5;
  return sqrt(2.0*PI) * pow(t,z+0.5) * exp(-t) * x;
}

float beta(in float a, in float b) {
  return (gamma(a)*gamma(b))/gamma(a+b);
}

vec2 inversesc(in vec2 z, in int K) {
  float cn[6];
  for (int n = 1; n <= 5; n++) {
    cn[n] = binomial(float(n)-1.0+2.0/float(K),n)/float(1+n*K);
  }
  float C = beta(1.0/float(K),1.0-2.0/float(K))/float(K);
  z *= C;
  vec2 h = cpow(z,int(K));
  float T1 = -cn[1];
  float T2 = -cn[2]+float(K+1)*pow(cn[1],2.0);
  float T3 = -cn[3]+float(3*K+2)*(cn[1]*cn[2]-float(K+1)/2.0*pow(cn[1],3.0));
  float T4 = -cn[4]+float(2*K+1)*(2.0*cn[1]*cn[3]+pow(cn[2],2.0)-float(4*K+3)*
                                  (pow(cn[1],2.0)*cn[2]-float(K+1)/3.0*pow(cn[1],4.0)));
  float T5 = -cn[5]+float(5*K+2)*(cn[1]*cn[4]+cn[2]*cn[3]+float(5*K+3)*
            (-0.5*pow(cn[1],2.0)*cn[3]-0.5*cn[1]*pow(cn[2],2.0)+float(5*K+4)*
            (pow(cn[1],3.0)*cn[2]/6.0-float(K+1)*pow(cn[1],5.0)/24.0)));
  vec2 X = vec2(1,0)+h/pow(C,float(K));
  vec2 w = cmul(z,vec2(1,0) + T1*h + T2*cpow(h,2) + T3*cpow(h,3) + T4*cpow(h,4) + cdiv(T5*cpow(h,5),X));
  return w;
}

// 2D
vec2 hexgrid2hex(in vec2 Z, out vec3 U_id) {
    const float
        SQRT3 = sqrt(3.),
        SQRT_3 = 1./SQRT3;
	const mat2
        M = mat2(SQRT_3,-1.,-SQRT_3,-1.),
        iM = 0.5*mat2(SQRT3,-SQRT3,-1.,-1.);
    Z = crot(Z,PI/6.);
    Z = iM*Z/0.75;
    vec3 U = vec3(Z.x,-Z.x-Z.y,Z.y);
    U_id = 2.*floor(U*0.5+0.5);
    vec3 U_lo = U-U_id;
    vec3 aU_lo = abs(U_lo);
    if (aU_lo.x > aU_lo.y && aU_lo.x > aU_lo.z) {
        U_lo.x = -U_lo.y-U_lo.z;
        U_id.x = -U_id.y-U_id.z;
    }
    if (aU_lo.y > aU_lo.z) {
        U_lo.y = -U_lo.x-U_lo.z;
        U_id.y = -U_id.x-U_id.z;
    }
    else {
        U_lo.z = -U_lo.x-U_lo.y;
        U_id.z = -U_id.x-U_id.y;
    }
    Z = U_lo.rb;
    Z = M*Z*0.75;
    return crot(Z,-PI/6.);
}

// 3D
vec3 rot3(in vec3 P, in vec3 A, in float a) {
    return mix( A*dot(P,A), P, cos(a) ) + sin(a)*cross(P,A);
}

#ifdef HELIX_HOLTSETIO
// --------------------------------------------------------
// holtsetio helix method
// --------------------------------------------------------
// https://www.shadertoy.com/view/3tjGDR
// "Infinite recursive helix"
#define KEPLER_MAXITER 2


// 
float solveKeplerHalley(float e,float M) {
    float E =clamp(M+PI,0.00,PI);
    int i=0;
    while(i<KEPLER_MAXITER)
    {
        float esinE = e*sin(E);
        float k0mM = (E-esinE)-M;
        float k1 = (1.0-e*cos(E));
        E -= (2.0*k0mM*k1)/(2.0*k1*k1-k0mM*(esinE));
        i++;
    }
    return E;
}

float solveKepler(float e, float M) {
    //http://www.jgiesen.de/kepler/kepler.html
    if (e >= 1.0) {
        return solveKeplerHalley(e,M);
    }
    float E = (e < 0.8 ? M : PI);
    float F = E - e*sin(M)-M;
    int i = 0;
    while (i < KEPLER_MAXITER) {
    	E -= F/(1.0 - e*cos(E));
        F = E - e*sin(E) - M;
        i++;
    }
	return E;
}

// Computes the closest point to p on a Helix (R,T) with n strands.
// The returned struct contains the closest point, the strand and the point Theta on the helix.
vec3 closestPointHelix(vec4 p, float R, float T, float n_helices) {
    // Nievergelt 2009
    // doi: 10.1016/j.nima.2008.10.006

    //Helix: H(Theta) = [R*cos(Theta), R*sin(Theta), T*Theta]
    //Point: D = (u, v, w) = [r * cos(delta), r * sin(delta), w]
    float delta = atan(p.y, p.x);
    float r = length(p.yx);
    float kt = ((p.z/T)-delta)/PI2;
    float inv_n_helices = 1.0/n_helices;
    float n = floor((fract(kt) + 0.5*inv_n_helices)/inv_n_helices);
    float s_offset = -n*inv_n_helices*PI2;
    float dktp = delta + round(kt-n*inv_n_helices) * PI2; 
    float M = PI + (p.z/T) + s_offset - dktp;
    float e = (r*R)/(T*T);
    float E = solveKepler(e,M);
    float Theta = E - PI + dktp;
    float theta = (Theta-s_offset);
    //res.strand=n;
    #ifdef WOBBLE
    	R*= 1.0+0.2*sin(0.01*iTime/(abs(T))+theta*3.0);
    #endif
    return vec3(R*cos(Theta), R*sin(Theta), theta*T);
}

vec3 closestHelixHolseito(vec3 p, float lead, float radius) {
	return closestPointHelix(vec4(p.zyx, 1.), radius, lead/6.3, 1.).zyx;
}
#else
// --------------------------------------------------------
// tdhooper helix method
// --------------------------------------------------------
// https://www.shadertoy.com/view/MstcWs by tdhooper 
// "Helix Distance" 

// repeat space along one axis
float pMod1(inout float p, float size) {
    float halfsize = size*0.5;
    float c = floor((p + halfsize)/size);
    p = mod(p + halfsize, size) - halfsize;
    return c;
}

// cartesian to polar coordinates
vec3 cartToPolar(vec3 p) {
    float x = p.x; // distance from the plane it lies on
    float a = atan(p.y, p.z); // angle around center
    float r = length(p.zy); // distance from center
    return vec3(x, a, r);
}

// polar to cartesian coordinates
vec3 polarToCart(vec3 p) {
    return vec3(
        p.x,
        sin(p.y) * p.z,
        cos(p.y) * p.z
    );
}

vec2 closestPointOnRepeatedLine(vec2 line, vec2 point){

    // Angle of the line
    float a = atan(line.x, line.y);

    // Rotate space so we can easily repeat along
    // one dimension
    point = crot(point, a);

    // Repeat to create parallel lines at the corners
    // of the vec2(lead, radius) polar bounding area
    float repeatSize = sin(a) * line.y;
    float cell = pMod1(point.x, repeatSize);

    // Rotate space back to where it was
    point = crot(point, -a);

    // Closest point on a line
    line = normalize(line);
    float d = dot(point, line);
    vec2 closest = line * d;

    // Part 2 of the repeat, move the line along it's
    // perpendicular by the repeat cell
    vec2 perpendicular = vec2(line.y, -line.x);
    closest += cell * repeatSize * perpendicular;

    return closest;
}

// closest point on a helix
vec3 closestHelix(vec3 p, float lead, float radius) {

    p = cartToPolar(p);
    p.y *= radius;

    vec2 line = vec2(lead, radius * PI * 2.);
    vec2 closest = closestPointOnRepeatedLine(line, p.xy);

    closest.y /= radius;
    vec3 closestCart = polarToCart(vec3(closest, radius));

    return closestCart;
}
#endif

// cartesian to helix coordinates
vec3 helixCoordinates(vec3 p, vec3 closest, float lead, float radius) {
    float helixAngle = atan((2. * PI * radius) / lead);
    vec3 normal = normalize(closest - vec3(closest.x,0,0));
    vec3 tangent = rot3(vec3(1,0,0), normal, -helixAngle);
    float x = (closest.x / lead) * radius * PI * 2.;
    float y = dot(p - closest, cross(tangent, normal));
    float z = dot(p - closest, normal);
    return vec3(x,y,z);
}

// BB
vec2 isphere(in vec3 ro, in vec3 rd, in float r) {
	float b = dot(ro,rd);
	float c = dot(ro,ro) - r*r;
    float h = b*b - c;   
    if( h<0.0 ) return vec2(-1.0);
    h = sqrt( h );
    return -b + vec2(-h,h);
}

// sdf
float sdBox( in vec3 p, in vec3 b ) {
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdCylinder( vec3 p, float r ) {
  return length(p.xz)-r;
}

float sdTorus( in vec3 p, in vec2 t ) {
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float opIntersection( in float d1, in float d2 ) {
    return max(d1,d2); }

float opSmoothUnion( in float d1, in float d2, in float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

// color
vec3 h2rgb(in float h) {
    vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
    return rgb*rgb*(3.0-2.0*rgb); // cubic smoothing
}

// srgb <--> rgb (linear)
vec3 rgb2srgb(vec3 col) {
    return pow(col, vec3(1./2.2));
}
