vec3 getMaterial( vec3 pp, float id_material);
vec3 light_pos1;  vec3 light_color1 ;
vec3 light_pos2;  vec3 light_color2 ;

//operacion de Union  por FabriceNeyret2
#define opU2(d1, d2) ( d1.x < d2.x ? d1 : d2 )

float sdSphere( vec3 p, float s )
	{ return length(p)-s;}
float sdBox( vec3 p, vec3 b )
	{ vec3 d = abs(p) - b;   return length(max(d,0.0))+ min(max(d.x,max(d.y,d.z)),0.0); }

///----------Operacion de Distancia--------
float Intersect(float distA, float distB)
	{ return max(distA, distB);}
float Union(float distA, float distB)
	{ return min(distA, distB);}
float Difference(float distA, float distB)
	{ return max(distA, -distB);}
//----------oPeraciones de Repeticion
float opRep1D( float p, float c )
	{ float q = mod(p+0.5*c,c)-0.5*c; return  q ;}
vec2 opRep2D( in vec2 p, in vec2 c )
	{ vec2 q = mod(p+0.5*c,c)-0.5*c; return  q ;}


// object transformation

vec3 rotate_x(vec3 p, float phi)
{   float c = cos(phi);	float s = sin(phi);
    return vec3(p.x, c*p.y - s*p.z, s*p.y + c*p.z);
}
vec3 rotate_y(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x + s*p.z, p.y, c*p.z - s*p.x);
}
vec3 rotate_z(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x - s*p.y, s*p.x + c*p.y, p.z);
}
vec2 rotatev2(vec2 p, float ang)
{   float c = cos(ang); float s = sin(ang);
    return vec2(p.x*c - p.y*s, p.x*s + p.y*c);
}


///oneshade    
vec2 Rotate(in vec2 p, in vec2 o, in float r) 
{   float c = cos(r), s = sin(r);
    return (p - o) * mat2(c, -s, s, c) + o;
}



vec2 GetDist(vec3 p  ) 
{	vec2 res= vec2(9999.0, -1.0);  
     p.y=p.y+10.0+iTime*10.0;
    vec3 p0=p;
    p.z-=45.0;
    //edificio
    p.y=opRep1D(p.y,32.0 );
    //hacer hueco de edificio
    float d1a=  sdBox( p, vec3(30,30,30) );
    float d1b=  sdBox( p, vec3(28,30,28) );
    float d1=Difference(d1a,d1b);
    p=p0;
    p.z-=45.0;
    //ventana xy
    p.x= opRep1D(p.x,8.0 );
    p.y= opRep1D(p.y,15.0 );
    float d2a=  sdBox( p, vec3(2.5,5.0,40) );
    d1=Difference(d1,d2a);


    //ventana zy
    p.x= opRep1D(p.z,8.0 );
    p.y= opRep1D(p.y,15.0 );
    float d2b=  sdBox( p, vec3(2.5,5.0,40) );
    d1=Difference(d1,d2b);

    res =opU2(res, vec2(d1,100.0));
    res =opU2(res, vec2(d1b,4.0));


    return res;
}

vec3 GetNormal(vec3 p)
{   float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(GetDist(p-e.xyy).x,GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);
    return normalize(n);
}

vec2 RayMarch(vec3 ro, vec3 rd, int PMaxSteps)
{   vec3 p;
    vec2 hit, object=vec2(0.1,0.0);
    for(int i=0; i <= PMaxSteps; i++) 
    { p = ro + rd*object.x;
      hit = GetDist(p);
      object.x += hit.x;
      object.y = hit.y;
      if (abs(hit.x) < EPSILON || object.x > MAX_DIST) break;
    }    
    return object;
}

float getSoftShadow(vec3 p, vec3 lightPos) {
    float res = 9999.0;
    float dist = 0.01;
    float lightSize = 0.03;
    for (int i = 0; i < MAX_STEPS; i++) {
      float hit = GetDist(p + lightPos * dist).x;
      res = min(res, hit / (dist * lightSize));
      dist += hit;
      if (hit < 0.0001 || dist > 60.0) break;
    }
    return clamp(res, 0.0, 1.0);
}

float occlusion(vec3 pos, vec3 nor)
{   float sca = 2.0, occ = 0.0;
    for(int i = 0; i < 10; i++) {    
      float hr = 0.01 + float(i) * 0.5 / 4.0;
      float dd = GetDist(nor * hr + pos).x;
      occ += (hr - dd)*sca;
      sca *= 0.6;
    }
    return clamp( 1.0 - occ, 0.0, 1.0 );    
}


vec3 lightingv3(vec3 normal,vec3 p, vec3 lp, vec3 rd, vec3 ro,vec3 lightColor, float t) 
{   vec3 lightPos=lp;
    vec3 worldPos = p;
       vec3 V = normalize(ro - p);
    vec3 N = normal;
    vec3 L = normalize (lightPos - worldPos);
    vec3 R = reflect (-L, N);

    float lightDist = max(length(L), .001);
    float atten=1.0 / (1.0 + lightDist * 0.125 + lightDist * lightDist * .05);
    L /= (lightDist*atten);
    float shadow = getSoftShadow(worldPos, L);// shadows

    float occ = occlusion(worldPos, N);// occ
    vec3 ambient = Ka + Ka * dot(normal, vec3(0., 1., 0.))*lightColor;
    ambient*=0.5;

    vec3 fresnel =  lightColor *  pow(clamp(1.0 + dot(rd, N), 0.0, 1.0), 2.0);;
    float diff= clamp(dot(N, L), 0.0, 1.0);
    vec3 diffuse =  lightColor * diff;
    float shininess=10.0;
    float specular    = pow(max(dot(R, V), 0.0), shininess);

    vec3 back = 0.5 * lightColor * clamp(dot(N, -L), 0.0, 1.0); // back
    vec3 colOut = occ*lightColor*(ambient+diffuse*shadow+.25 +back) + vec3(.5)*specular*specular;
    return colOut;
}


vec3 Getluz(vec3 ro,vec3 p, vec3 lp, vec3 lcol,vec3 n, float diffp, vec3 specp)
{
    vec3 v=normalize(ro-p);
    vec3 h=normalize(lp+v);
    vec3 l=normalize(lp-p);
    float ndh=max(0.0,dot(n,h));
    float ndl=max(0.0,dot(n,l));
    float ndv=max(0.0,dot(n,v));

    float diff=ndl*diffp;
    vec3  spec=0.8*pow(ndl,10.0)*specp;
    vec3 col=(diff+spec*lcol);
    return col;

}


vec3 getColorTextura( vec3 p, vec3 nor,  int i)
{	vec3 col;
    if (i==100 )
    {    p.y=p.y+10.0+iTime*10.0;
        col=tex3D(iChannel0, p/64., nor); return col*2.0; 
    }
	return vec3(0.0);
}

///---------------
float Speed = .03;
vec3 Light = vec3(.6,.2,.8);

float Map(vec3 Position)
{
    vec3 P = (Position*0.5+texture(iChannel1,Position*2.+iTime*Speed*.2).xyz*.02);

    float C = texture(iChannel1,P).r;
    C *= texture(iChannel1,P*vec3(.5,1,.5)).g;
    C = C*.9+.1*pow(texture(iChannel1,P*5.1).a,2.);
    return max((C-.3)*sqrt((Position.z-.1)/.3),0.)/.5;
}

vec3 NubesXor(in vec2 Coord, float pz)
{
    vec4 Color;
    vec3 R = vec3((Coord-.5*iResolution.xy)/iResolution.y,1.0);
    vec3 P = vec3(0.+sin(iTime*0.5),iTime*Speed,pz);

    vec4 C = vec4(0);
    for(float I = .2;I<.5;I+=.01)
    {
        float M1 = Map(P+R*I);
        float M2 = Map(P+R*I+Light*.01);
        C += vec4((.6+vec3(.6,.5,.4)*(exp(-M2*10.)-M1)),1)*M1*(1.-C.a);
        if (C.a>.99) break;
    }
	Color = C+vec4(vec3(.5,.7,.9)-R.y*.4,1)*(1.-C.a);
    return Color.xyz;
}
///--------------

vec3 render_sky_color(vec3 rd, vec2 frag)
{   vec3 sky; 
     float tt=iTime*20.;
     frag= rotatev2(frag,  radians(tt));


    sky=NubesXor(frag+rd.xz, 1.0); 
    return sky; 
    }


vec3 GetMaterial(vec3 p,  vec3 nor, vec3 ro,  vec3 rd, int id_color)
{  	vec3 colobj; 
     //if (id_color<40) { colobj=getColor(int( id_color));  return colobj; }

   if (id_color==4) { colobj=vec3(0.0,0.0,1.0);  return colobj; }

   if (id_color>=100 )
   { return  getColorTextura( p, nor,id_color); }

  return vec3(0.0);
}

vec3 linear2srgb(vec3 c) 
{ return mix(12.92 * c,1.055 * pow(c, vec3(1.0/1.8)) - 0.055, step(vec3(0.0031308), c)); }

vec3 exposureToneMapping(float exposure, vec3 hdrColor) 
{ return vec3(1.0) - exp(-hdrColor * exposure); }

vec3 ACESFilm(vec3 x)
{   float a,b,c,d,e;
    a = 2.51; b = 0.03; c = 2.43;d = 0.59; e = 0.14;
    return (x*(a*x+b))/(x*(c*x+d)+e);
}


vec3 Render(vec3 ro, vec3 rd, vec2 frag)
{  vec3 col = vec3(0);
 vec3 p;
     vec2 hit=RayMarch(ro,rd, MAX_STEPS);
      if(hit.x<MAX_DIST)
       {   p = (ro + rd * hit.x );
        vec3 nor=GetNormal(p);
        vec3 colobj;
        colobj=GetMaterial( p, nor, ro, rd,  int(hit.y));
        vec3 result;

        result= lightingv3(nor, p,light_pos1, rd,ro,colobj,hit.x)*light_color1;
        result+= lightingv3(nor, p,light_pos2,rd, ro,colobj,hit.x)*light_color2;
        col= result/2.0;
        col= (ACESFilm(col)+linear2srgb(col)+col+ exposureToneMapping(3.0, col))/4.0 ;


    }
    else if(hit.x>MAX_DIST)
    col= render_sky_color(rd,frag);
   return col;
}

mat3 setCamera(vec3 ta, vec3 ro, float cr) {
    vec3 z = normalize(ta - ro);
    vec3 cp = vec3(sin(cr), cos(cr), 0.);
    vec3 x = normalize(cross(z, cp));
    vec3 y = cross(x, z);
    return mat3(x, y, z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    vec2 uv = fragCoord.xy / iResolution.xy - 0.5;
     uv.x = uv.x * iResolution.x / iResolution.y;
    light_pos1= vec3(-10.0, 120.0, -25.); light_color1=vec3( 1.0,1.0,1.0 );
 	light_pos2= vec3(10.0, 20.0, -25.0 ); light_color2 =vec3( 1.0,1.0,1.0 ); 

   float tt=iTime*20.;
   vec3 ro=vec3(60.0,7.0,-100.0);
   ro= rotate_y(ro, radians(tt));
    vec3 ta = vec3(60.,0.,60.);
    mat3 cam = setCamera(ta, ro, 0.0);
    vec3 rd = normalize(cam * vec3(uv, 1.0));
    vec3 col= Render( ro,  rd,fragCoord);
    fragColor = vec4(col,1.0);
}

//-------common
#define PI 3.14159265


///Shane...
vec3 tex3D( sampler2D tex, in vec3 p, in vec3 n ){    
  n = max(n*n - .2, .001); // max(abs(n), 0.001), etc.
  n /= dot(n, vec3(1)); 
  vec3 tx = texture(tex, p.yz).xyz;
  vec3 ty = texture(tex, p.zx).xyz;
  vec3 tz = texture(tex, p.xy).xyz;
  return mat3(tx*tx, ty*ty, tz*tz)*n; 
}
