mat2 Rot(float a){
    float s=sin(a), c=cos(a);
    return mat2(c,-s,s,c);
}

float Xor(float a, float b){
 return a*(1.-b)+   b*(1.-a);
}

float HexDist(vec2 p){
	p = abs(p);
    float c= dot(p,normalize(vec2(1,1.73)));
    c=max(c,p.x);   
    return c;
}


const float circle_r = 1.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = (uv-.5)*2.;
    uv.x *= iResolution.x/iResolution.y;


    vec2 center = vec2(iMouse.x, iMouse.y);
    center = ((center/iResolution.xy)-.5)*2.;
    center.x *= iResolution.x/iResolution.y;

    float dis = distance(uv, center);
    float d = pow(dis, 8.);//step(circle_r, dis);
    //float cir = 1.-d;

    float filt = pow(abs(sin(uv.x+uv.y+iTime*2.)), 4./circle_r);

    float cir2 = smoothstep((1.+.01)*circle_r, circle_r, dis) - smoothstep(circle_r, circle_r*(1.-.15), dis);
    float cir3 = smoothstep((1.+.01)*circle_r, circle_r, dis) - smoothstep(circle_r, circle_r*(1.-.35), dis);

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    uv-=center;
    uv*=pow(d, .1);
    uv*=((8.-(circle_r-1.)*4.)/circle_r);
    float col1 = 0.;
    d=HexDist(uv);
    float n = floor(mod(uv.y, 2.));
    uv.x+=n*.5;

    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);

    float m=0.1;
    float c=0.1;
    float t = iTime;

    for(float y=-1.; y<=1.; y++) {
    	for(float x=-1.; x<=1.; x++) {
    		vec2 offs = vec2(x,y);
            float d=HexDist(gv+offs);
            float dist=length(id-offs)*.3;
            float r = mix(.4,.4,sin(dist)*.5+.5);
            c=Xor(c,smoothstep(r,r*0.95,d));
    	}
    }
    col1+=c;
    col1*=cir3;



    float res = filt*col1+cir2;

    fragColor = vec4(col*vec3(res),res);
}
