#define antialiasing(n) n/min(iResolution.y,iResolution.x)
#define S(d,b) smoothstep(antialiasing(0.8),b,d)
#define S2(d,b) smoothstep(6.0*antialiasing(0.5),b,d)
#define PI     3.14159265


float sdCircle( vec2 p, float r )
{ return length(p) - r;}

vec3 DrawFigBorde(vec3 pColObj, vec3 colOut, float distObj )
{ colOut = mix(colOut,pColObj ,S2( distObj,0.0));
  colOut = mix(colOut,vec3(0.0) ,S2(abs( distObj)-0.005,0.0));
  return colOut;
}

vec3 DrawFigBordev2(vec3 pColObj, vec3 colOut, float distObj )
{ colOut = mix(colOut,pColObj ,S2( distObj+0.05,0.0));
  colOut = mix(colOut,vec3(0.0) ,S2(abs( distObj)-0.05,0.0));
  return colOut;
}

vec3 DrawFigBordeCol(vec3 pColObj, vec3 colOut, float distObj , vec3 colBorde )
{ colOut = mix(colOut,pColObj ,S2( distObj+0.05,0.0));
  colOut = mix(colOut,colBorde ,S2(abs( distObj)-0.05,0.0));
  return colOut;
}

///oneshade    
vec2 Rotate(in vec2 p, in vec2 o, in float r) 
{   float c = cos(r), s = sin(r);
    return (p - o) * mat2(c, -s, s, c) + o;
}

vec3 spiro(vec2 p, vec3 col)
{   float d1,d2,d3;
    float angrad;
    vec2 p2,p3;
     int n, nmax;
    nmax=30;
    float ang=360.0/float(nmax);
    vec2 pos=vec2(2.5,0.0);

    float df=9999.9;
    float df2=9999.9;

    for(n=0; n<nmax;n++)
    {
        float t=iTime*60.0;
        t=radians(t);
        angrad=radians(ang*float(n) )+t;
        p2=Rotate(p, pos, angrad);
        d2= sdCircle(p2,3.5+1.5*sin(t)); 
        d2=abs(d2)-0.01;
        df=min(df,d2);
    }

    col= DrawFigBordeCol(vec3(0.0,1.0,0.0),col,df,vec3(0.0,0.15,0.5));
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   vec2 uv = -1.0 + 2.0 * fragCoord.xy/iResolution.xy;
	uv.x *= iResolution.x/iResolution.y;
    uv-=vec2(-0.3,0.0);
    float esc=8.0;
    uv*=esc;
    vec2 uv0=uv;
    vec3 col=vec3(1.0);
    col= spiro(uv,col);
    col=pow(col,  vec3(0.554545));
    fragColor = vec4(col,1.0);
}
