void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = texture(iChannel0, uv);
}

float f_n_sd_line_segment(in vec2 p, in vec2 a, in vec2 b) {
	vec2 ba = b - a;
	vec2 pa = p - a;
	float h = clamp(dot(pa, ba) / dot(ba, ba), 0., 1.);
	return length(pa - h * ba);
}

vec2 f_n_o_rnd_hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_res = iResolution.xy;
    float n_tau = radians(360.);
    float n_scl_min = min(o_res.x, o_res.y);
    vec2 o_trn = (fragCoord.xy-o_res*.5)/n_scl_min;
    vec2 o_trn_mou = (iMouse.xy-o_res*.5)/n_scl_min;
    if(sign(iMouse.z) == 0.){
        o_trn_mou = vec2(
            sin(iTime*2.), 
            cos(iTime*1.)
        )*.5;
    }

    vec2 o_trn1 = fragCoord/iResolution.xy;
    float naa = 1./n_scl_min;

    //max radius circle 
    float nrm = 0.05;
    float n_its = 30.;
    vec2[
        60 //needs to be n_its*2.
    ] aop;

    if(iFrame == 0){

        fragColor = (vec4(
            f_n_o_rnd_hash22(fragCoord.xy),
            f_n_o_rnd_hash22(fragCoord.yx)
        )-.5);
        float n_it_nor = fragCoord.x/n_its;
        fragColor.z = (1.-n_it_nor)*0.06;//+(sin(((fragCoord.x/n_its)+.75)*n_tau)*.5+.5)*.1;
        return;
    }
    if(fragCoord.y < 1.){
        vec2 oank = o_trn_mou.xy;
        vec4 op = texelFetch(
               iChannel0, 
               ivec2(int(fragCoord.x), 0), 
               0
        );
        float nr = nrm;
        if(fragCoord.x > 1.){
           oank = texelFetch(
               iChannel0, 
               ivec2(int(fragCoord.x)-1, 0), 
               0
            ).xy;
           nr = op.z;

        }
        vec2 op2 = op.xy-oank.xy;
        float nd1 = length(op2);
        float ntmp = nr;
        if(nd1 > ntmp){
            op.xy = oank.xy + normalize(op2.xy)*ntmp;
        }
        fragColor = op;


        return;
    }

    float ndm = length(o_trn-o_trn_mou);
    ndm = smoothstep(0., naa*4., abs(ndm-nrm)); 

    float ndp_min = 1.;
    float n_it_nor_one = (1./n_its);



    for(float n_it_nor = 0.; n_it_nor < 1.; n_it_nor+=n_it_nor_one){
       vec2 op1 = o_trn_mou.xy;
       if(n_it_nor >= n_it_nor_one){
           op1 = texelFetch(
               iChannel0, 
               ivec2(int((n_it_nor-n_it_nor_one)*n_its), 0), 
               0
           ).xy;
       }

       vec4 op = texelFetch(
           iChannel0, 
           ivec2(int(n_it_nor*n_its), 0), 
           0
       );
       vec2 odelta = op.xy-op1.xy;
       vec2 oortho = vec2(odelta.y, -odelta.x);

       float ndline = f_n_sd_line_segment(o_trn,op1.xy, op.xy);
       float ndp = length(o_trn - op.xy);
       ndp = smoothstep(0., naa*4., abs(ndp-op.z)); 
       ndp *= smoothstep(0., naa*4., ndline);
       vec2 ob = op1.xy+normalize(oortho.xy)*op.z;
       float ndlineortho = f_n_sd_line_segment(o_trn,op.xy, op.xy+oortho.xy);
       ndp *= smoothstep(0., naa*4., ndlineortho);
       float ndlineortho2 = f_n_sd_line_segment(o_trn,op.xy, op.xy-oortho.xy);
       ndp *= smoothstep(0., naa*4., ndlineortho2);
       aop[int(n_it_nor*n_its*2.)+0] = op.xy-oortho.xy;
       aop[int(n_it_nor*n_its*2.)+1] = op.xy+oortho.xy;

       ndp_min = min(ndp, ndp_min);

    }
    float n_minp = 1.;
    for(float n_it_nor = 0.; n_it_nor < 1.; n_it_nor+=n_it_nor_one){
        if(n_it_nor+n_it_nor_one >=1.){continue;}
        vec2 op1 = aop[int(n_it_nor*n_its*2.)+0];
        vec2 op2 = aop[int(n_it_nor*n_its*2.)+1];
        vec2 opl1 = aop[int((n_it_nor+n_it_nor_one)*n_its*2.)+0];
        vec2 opl2 = aop[int((n_it_nor+n_it_nor_one)*n_its*2.)+1];
        float ndl = f_n_sd_line_segment(
            o_trn, 
            op1, 
            opl1
        );
        n_minp *= smoothstep(0., naa*4., ndl);
        float ndl2 = f_n_sd_line_segment(
            o_trn, 
            op2, 
            opl2
        );
        n_minp *= smoothstep(0., naa*4., ndl2);
    }
    fragColor = vec4(
        //0.,//ndp_min*ndm, 
        n_minp*ndm
        //1., 
        //1.
    );
    fragColor = 1.-fragColor;

}
