#define h(U)  fract( 1e4* sin( dot(U,vec2(113,7))) )

void mainImage( out vec4 O, vec2 u )
{
    O -= O;
    vec2 U = u/iResolution.xy * 12. - vec2(5.5,1),
         z = U;

    for( float p, i=0.; i<256.; i++ )
        p = h(U+i+iTime),
        z =   p < .01 ? z * vec2( 0, .16 )
            : p < .84 ? z * mat2( .85, .04,-.04, .85) + vec2( 0, 1.6 )
            : p < .92 ? z * mat2( .20,-.26, .23, .22) + vec2( 0, 1.6 )
            :           z * mat2(-.15, .28, .26, .24) + vec2( 0,  .4 ),
        i>32. ? O += exp(-1e2*dot(U-z,U-z)) : O;
}

