void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy/iResolution.xy);
    fragColor = texture(iChannel0, uv);
}

//------------------------------------------------------------------------
//  End of time.  A 4k intro by Virgill/Alcatraz & KK/Altair
//
//  Full intro: http://www.pouet.net/prod.php?which=77102
//  Youtube: https://youtu.be/5lR76o9lWB0
//  4klang music: https://soundcloud.com/virgill/4klang-end-of-time
//  Thanks to these ppl for help + inspiration: Slerpy, LJ, Xtr1m, Gopher
//------------------------------------------------------------------------

vec3 scol;

void dmin(inout vec3 d, float x, float y, float z)
{
	if(x<d.x) d=vec3(x,y,z);
}

// 3D noise function (IQ, Shane)
float noise(vec3 p)
{
	vec3 ip=floor(p);
	p-=ip;
	vec3 s=vec3(7, 157, 113);
	vec4 h=vec4(0., s.yz, s.y+s.z)+dot(ip, s);
	p=p*p*(3.-2.*p);
	h=mix(fract(sin(h)*43758.5), fract(sin(h+s.x)*43758.5), p.x);
	h.xy=mix(h.xz, h.yw, p.y);
	return mix(h.x, h.y, p.z);
}

// hemisphere hash function based on a hash by Slerpy
vec3 hashHs(vec3 n, float seed)
{
	float a = fract(sin(seed)*43758.5)*2.-1.;
	float b = 6.283*fract(sin(seed)*41758.5)*2.-1.;
	float c=sqrt(1.-a*a);
	vec3 r=vec3(c*cos(b), a, c*sin(b));
	return r;
}

float sdTorus(vec3 p, vec2 t)
{
	return length(vec2(length(p.yz)-t.x, p.x))-t.y;
}

float box(vec2 p)
{
	p=abs(p); return max(p.x, p.y);
}

void pR(inout vec2 p, float a)
{
	p = cos(a)*p+sin(a)*vec2(p.y, -p.x);
}


vec3 map(vec3 p)
{
    vec3 q;
    vec3 d = vec2(0, 1.).yxx;
	dmin(d, length(p+vec3(cos(0.1*iTime),sin(0.1*iTime),2.5+sin(0.5*iTime)))-.3, 1., .5); 
	dmin(d, length(p+vec3(cos(0.3*iTime),sin(0.3*iTime),2.5-sin(0.5*iTime)))-.3, 1.,-.5); 
    q=p;
    q.xy = vec2( (atan(q.x,q.y)+0.*sin(0.9*sqrt(dot(q.xy,q.xy))-0.5*iTime)),1.-length(q.xy));   
    dmin(d, .8-abs(q.y)-.6*noise(5.*q+0.1*iTime)-0.05*noise(40.*q-iTime), 0., 1.); 
	return d;

}


vec3 normal(vec3 p)
{
	float m = map(p).x;
	vec2 e = vec2(0,.05);
	return normalize(m-vec3(map(p - e.yxx).x, map(p - e.xyx).x, map(p - e.xxy).x));
}


void madtracer(in vec3 ro1, in vec3 rd1, in float seed)
{
	scol = vec3(0);	
	float t = 0., t2 = 0.;
	vec3 m1, m2, rd2, ro2, nor2;
	vec3 roold=ro1;
	vec3 rdold=rd1;
	m1.x=0.;
	for( int i = 0; i < 140; i++ )
	{

		seed = fract(seed+iTime*float(i+1)+.1);
		//ro1=mix(roold, hashHs(ro1, seed), 0.001);				// antialiasing
        //rd1=mix(rdold,hashHs(rdold,seed),0.06*m1.x); 	
		m1 = map(ro1+rd1*t);
		t+=m1.z!=0. ? 0.25*abs(m1.x)+0.0008 : 0.25*m1.x;
		ro2 = ro1 + rd1*t;
		nor2 = normal(ro2); 									// normal of new origin
		seed = fract(seed+iTime*float(i+2)+.1);
        rd2 = mix(reflect(rd1, nor2), hashHs(nor2, seed), m1.y);// reflect depending on material
		m2 = map(ro2+rd2*t2);
		t2+=m2.z!=0. ? 0.25*abs(m2.x) : 0.25*m2.x;
		scol+=.007*(vec3(1.+m2.z, 1., 1.-m2.z)*step(1., m2.y)+vec3(1.+m1.z, 1., 1.-m1.z)*step(1., m1.y));
	}
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{


	vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor=vec4(0.0);
	// borders
	if( uv.y>.2&&uv.y<0.8)
	{
		float seed = sin(fragCoord.x + fragCoord.y)*sin(fragCoord.x - fragCoord.y);
		vec3 bufa= texture(iChannel0, uv).xyz;

		// camera
		vec3 ro, rd;
		vec2 uv2 = (2.*fragCoord.xy-iResolution.xy)/iResolution.x;
		ro = vec3(0, 0,-5);
		rd = normalize(vec3(uv2, 1));
		// rotate scene
		pR(rd.xz, .4*-sin(.11*iTime));
		pR(rd.yz, .3*sin(.13*iTime));
		pR(rd.xy, .1*-cos(.15*iTime));
		// render    
		madtracer(ro, rd, seed);

		fragColor =clamp(vec4(0.7*scol+0.7*bufa, 0.), 0., 1.); // with blur
	}
}
