/*
 Just stick this little utility function right before the
 last assignment of fragment color to filter out any invisible 
 NaNs and replace them with a very obvious repeating "NAN" pattern.
*/
vec4 notnan(vec4 v){
	bvec4 nan=bvec4(v.r!=v.r,v.g!=v.g,v.b!=v.b,v.a!=v.a);
	if(nan.r||nan.g||nan.b||nan.a){
		vec2 p=floor(mod(vec2(18.*mod(floor(
		gl_FragCoord.y/18.),10.)+15.,5)+gl_FragCoord.xy/vec2(4,3),vec2(14,6)));
		v=(p.x>10.||p.y>4.||(mod(p.x,2.)==1.&&((p.x!=1.&&p.x!=9.)||p.y!=4.)&&(p.x!=5.||(
		p.y!=2.&&p.y!=4.))))?vec4(0,0,0,1):vec4(vec3((floor(mod(
		gl_FragCoord.y,3.))==0.&&(mod(p.x,2.)==1.||p.y==0.)?.5:1.)*(
		(nan.a?.65:.15)+(nan.a?-.3:.8)*vec3(nan.rgb))),1);
	}
	return v;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 col = texture(iChannel0, fract((fragCoord-iMouse.xy)/iResolution.xy));
	fragColor = col*6.0*col.w;
    if(abs(fragCoord.x-iMouse.x)>50.0) fragColor = notnan(fragColor);
}

#define rnd(U) hash12(U)

#define T(i,j) texture(iChannel0, fract( ( U +vec2(i,j) ) / iResolution.xy) )

#define PREVIEW false
/*
#define PREVIEW (iResolution.y < 250.0)
