#define K 0.06
#define L 0.52
#define M 2.65
//*/

#define K (-6.0-9.5*(fract(iTime/20.0)))
#define L -0.4
#define M -0.3

#define TIMESTEP (PREVIEW ? 1.0 : 0.2)

// https://www.shadertoy.com/view/4djSRW
#define HASHSCALE1 .1031
float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * HASHSCALE1);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

void mainImage( out vec4 O,  vec2 U )
{
    vec2 uv = U/iResolution.xy;
    if (mod(iTime,20.0)<0.5 && !PREVIEW)
    {
        O = vec4(0.0);
        return;
    }
    vec4 old = T(0, 0);
	O = ( T(-1, 1) + T(0, 1) + T(1, 1) 
        + T(-1, 0) + T(0, 0) + T(1, 0) 
        + T(-1,-1) + T(0,-1) + T(1,-1) ) / 9.;
    O *= 2.0-O;

    if (iFrame==0) O = vec4(.4);
    float time = mod(iTime, 1000.01246341);
    for (int i=0; i<3; ++i)
    {
        if (rnd(U-9.*time+.0+0.03*float(i))>.9999) O[i] = 1.0;
        if (rnd(U-9.*time+.1+0.03*float(i))>.9999) O[i] = 0.0;
    }
    O.w = mix(O.w, dot(O.xyz, O.yzx)/3.0, K)*L;
    O.xyz -= O.w*(O.xyz+M);
    for (int i=0; i<3; ++i)
    {
    	if (O[i] < 0.0) O[i] = 0.0;
    }
    O = mix(old, O, TIMESTEP);
}
