// Crystal Drops
// by Michal 'spolsh' Klos 2017

// Works well with Pulfrich Effect:
// https://en.wikipedia.org/wiki/Pulfrich_effect
// https://www.youtube.com/watch?v=Q-v4LsbFc5c

// comment line 5 in Buf A to animate camera endlessly

#define R iResolution
#define T iTime
#define F gl_FragCoord
#define M iMouse

// uncomment to enable FPS counter 
// #define FPS_COUNTER

//-----------------------------------------------------------------
// Digit drawing function by P_Malin (https://www.shadertoy.com/view/4sf3RN)

float SampleDigit(const in float n, const in vec2 vUV)
{		
	if(vUV.x  < 0.0) return 0.0;
	if(vUV.y  < 0.0) return 0.0;
	if(vUV.x >= 1.0) return 0.0;
	if(vUV.y >= 1.0) return 0.0;

	float data = 0.0;

	     if(n < 0.5) data = 7.0 + 5.0*16.0 + 5.0*256.0 + 5.0*4096.0 + 7.0*65536.0;
	else if(n < 1.5) data = 2.0 + 2.0*16.0 + 2.0*256.0 + 2.0*4096.0 + 2.0*65536.0;
	else if(n < 2.5) data = 7.0 + 1.0*16.0 + 7.0*256.0 + 4.0*4096.0 + 7.0*65536.0;
	else if(n < 3.5) data = 7.0 + 4.0*16.0 + 7.0*256.0 + 4.0*4096.0 + 7.0*65536.0;
	else if(n < 4.5) data = 4.0 + 7.0*16.0 + 5.0*256.0 + 1.0*4096.0 + 1.0*65536.0;
	else if(n < 5.5) data = 7.0 + 4.0*16.0 + 7.0*256.0 + 1.0*4096.0 + 7.0*65536.0;
	else if(n < 6.5) data = 7.0 + 5.0*16.0 + 7.0*256.0 + 1.0*4096.0 + 7.0*65536.0;
	else if(n < 7.5) data = 4.0 + 4.0*16.0 + 4.0*256.0 + 4.0*4096.0 + 7.0*65536.0;
	else if(n < 8.5) data = 7.0 + 5.0*16.0 + 7.0*256.0 + 5.0*4096.0 + 7.0*65536.0;
	else if(n < 9.5) data = 7.0 + 4.0*16.0 + 7.0*256.0 + 5.0*4096.0 + 7.0*65536.0;

	vec2 vPixel = floor(vUV * vec2(4.0, 5.0));
	float fIndex = vPixel.x + (vPixel.y * 4.0);

	return mod(floor(data / pow(2.0, fIndex)), 2.0);
}

float PrintInt(const in vec2 uv, const in float value )
{
	float res = 0.0;
	float maxDigits = 1.0+ceil(log2(value)/log2(10.0));
	float digitID = floor(uv.x);
	if( digitID>0.0 && digitID<maxDigits )
	{
        float digitVa = mod( floor( value/pow(10.0,maxDigits-1.0-digitID) ), 10.0 );
        res = SampleDigit( digitVa, vec2(fract(uv.x), uv.y) );
	}

	return res;	
}

float nrand(vec2 n)
{
	return fract(sin(dot(n.xy, vec2(12.9898, 78.233)))* 43758.5453);
}

vec3 filmGrainColor(vec2 uv, float offset)
{ // by ma (lstGWn)
    vec4 uvs;
    uvs.xy = uv + vec2(offset, offset);
    uvs.zw = uvs.xy + 0.5*vec2(1.0 / iResolution.x, 1.0 / iResolution.y);

    uvs = fract(uvs * vec2(21.5932, 21.77156).xyxy);

    vec2 shift = vec2(21.5351, 14.3137);
    vec2 temp0 = uvs.xy + dot(uvs.yx, uvs.xy + shift);
    vec2 temp1 = uvs.xw + dot(uvs.wx, uvs.xw + shift);
    vec2 temp2 = uvs.zy + dot(uvs.yz, uvs.zy + shift);
    vec2 temp3 = uvs.zw + dot(uvs.wz, uvs.zw + shift);

    vec3 r = vec3(0.0, 0.0, 0.0);
    r += fract(temp0.x * temp0.y * vec3(95.4337, 96.4337, 97.4337));
    r += fract(temp1.x * temp1.y * vec3(95.4337, 96.4337, 97.4337));
    r += fract(temp2.x * temp2.y * vec3(95.4337, 96.4337, 97.4337));
    r += fract(temp3.x * temp3.y * vec3(95.4337, 96.4337, 97.4337));

    return r * 0.25;
}

vec2 barrelDistortion(vec2 coord, float amt, float zoom)
{ // based on gtoledo3 (XslGz8)
  // added zoomimg
	vec2 cc = coord-0.5;
    vec2 p = cc*zoom;
    coord = p+0.5;
	float dist = dot(cc, cc);
	return coord +cc*dist*amt;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    fragColor = vec4(0.0);
    vec2 uv = F.xy/R.xy;
    vec2 vv = 2.0*(uv-0.5);
    vv.x *= R.x/R.y;

    float cas = step(abs(vv.y)*2.39,R.x/R.y);
    if (cas<0.1) return;

    vec3 c = texture(iChannel0, uv).rgb;   

    uv = barrelDistortion(uv, 0.1, 0.96);

    // chromatic abberation inspired by knifa (lsKSDz)
    vec2 d = abs((uv-0.5)*2.0);    
    d = pow(d, vec2(1.5, 1.0));
    d.y *= 0.1;

    float dScale = 0.01;
    const int maxSamples = 4;
    vec4 r, g, b;
    r = g = b = vec4(0.0);
    for (int i=0; i<maxSamples; ++i)
    {
        float rnd = nrand(uv+vec2(i)+0.001*T);
    	r += texture(iChannel0, uv +d*rnd*dScale);
    	g += texture(iChannel0, uv);
    	b += texture(iChannel0, uv -d*rnd*dScale);
	}

    c = vec3(r.r, g.g, b.b)/vec3(maxSamples);

    c *= 1.0 -0.25*filmGrainColor(0.5*uv, T).rgb;

    c = pow(c, vec3(0.4545));

    vec2 v = 2.*(uv-.5);
    v.y *= 2.39 * R.y/R.x;
    v = clamp((v*.5)+.5, 0., 1.);
    // c.rgb = vec3(1.0); // uncomment to see only vignette
    c *= 0.25 + 0.75*pow( 16.0*v.x*v.y*(1.0-v.x)*(1.0-v.y), 0.25);

#ifdef FPS_COUNTER    
    vec2 h = F.xy/R.xy; 
    h.x *= R.x / R.y;
    c += PrintInt( (h -vec2(0.0,0.21))*30.0, iFrameRate );
#endif 

    fragColor = vec4(c, 1.0);
}

// Crystal Drops
// by Michal 'spolsh' Klos 2017

// comment to animate camera endlessly
// #define SLOMO_LOOP

#define R iResolution
#define F gl_FragCoord
#define M iMouse

#define HASHSCALE4 vec4(.1031, .1030, .0973, .1099)

float T = 0.0;

vec4 hash41(float p)
{ // by Dave_Hoskins
	vec4 p4 = fract(vec4(p) * HASHSCALE4);
    p4 += dot(p4, p4.wzxy+19.19);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);    
}

// polynomial smooth min (k = 0.1);
float smin( float a, float b, float k )
{ // by iq
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float map(vec3 p)
{
    float s = length(p -vec3(0.0, .01*sin(8.0*T), 0.0)) - 0.5;
	s += 0.005*sin(45.0*p.x+10.0*T);

    for (int i=0; i<12; ++i) {
        vec4 rnd = hash41(100.0+float(i));        
        vec3 rndPos = 2.0*(normalize(rnd.xyz) -vec3(0.5));
        rndPos.y *= 0.2;
        float timeOffset = rnd.w;
        float phase = fract(timeOffset -0.25*T);
		vec3 offset = mix( 0.1*rndPos, 15.0*rndPos, phase);
        float rnd2 = fract(rnd.x +rnd.y);
        float s0 = length(p +offset) -0.25*mix(0.8 +0.2*rnd2, 0.2 +0.8*rnd2, phase);
        s = smin(s, s0, 0.4);
    }

    s += 0.002*sin(20.0*p.x +10.0*T);

    return s;    
}

vec3 env(vec3 dir) 
{
    vec3 cubemap = texture(iChannel0, dir).rgb;
    float ex = mix(6.0, 12.0, 0.5*(sin(0.5*T) +1.0));
    float t0 = 0.05*pow(1.0 -dot(vec3(0.0, -1.0, 0.0), dir), ex);
    float t1 = mix(0.2, 2.5, 1.0 -abs(sin(2.0*3.14*dir.y)));    
    vec3 c = cubemap *t0 *t1;
    return c * vec3(0.35, 1.2, 2.5);
}

vec3 calcNormal(vec3 p)
{ // by iq
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
	return normalize(	e.xyy *map(p + e.xyy) + 
						e.yxy *map(p + e.yxy) + 					  
						e.yyx *map(p + e.yyx) + 					  
				  	  	e.xxx *map(p + e.xxx) );
}

mat3 setCamera(in vec3 ro, in vec3 ta, float cr)
{ // by iq
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize(cross(cw,cp));
	vec3 cv = normalize(cross(cu,cw));
    return mat3(cu, cv, cw);
}

vec3 tonemapping(vec3 color)
{ // by Zavie (lslGzl)
	color = max(vec3(0.), color-vec3(0.004));
	color = (color * (6.2*color+.5))/(color*(6.2*color+1.7)+0.06);
	return color;
}

float spline(float x, float x1, float x2, float y1, float dy1, float y2, float dy2)
{
	float t = (x -x1) / (x2 -x1);	
    float a = 2.0*y1 -2.0*y2 +dy1 +dy2;
	float b = -3.0*y1 +3.0*y2 -2.0*dy1 -dy2;
	float c = dy1;
	float d = y1;
	float t2 = t*t;
	float t3 = t2*t;
	return a * t3 +b*t2 +c*t +d;
}

void calcTime()
{
    T = iTime;
#ifdef SLOMO_LOOP
    // slomo by Dave (4s23RW)
    T = mod(iTime, 20.0);
	const float slomoMin = 2.3;
	const float slomoMax = 2.6;
	const float slomoK = 0.2;
	const float slomoDuration = (slomoMax-slomoMin)/slomoK;
	if (T >= slomoMin && T<slomoMin+slomoDuration)
		T = spline(T, slomoMin, slomoMin+slomoDuration, slomoMin, slomoDuration*0.3, slomoMax, slomoDuration*0.15);
	else if ( T >= slomoMin+slomoDuration)
		T = T -slomoDuration +(slomoMax-slomoMin);
#endif
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    fragColor = vec4(0,0,0,1);

    calcTime();

    vec2 u = F.xy/R.xy;
    vec2 v = 2.0*(u -0.5);
    v.x *= R.x/R.y;
    vec2 m = M.xy/R.xy;
    float cas = step(abs(v.y)*2.39,R.x/R.y);
    if (cas<0.1) return;

	vec3 ro = vec3(
        8.0*cos(0.2*T +6.0*m.x),
        2.0*mix(-1.0,1.0, m.y),
        8.0*sin(0.2*T +6.0*m.x)
    );
    float taAnim = 2.0*(smoothstep(-0.1, 0.1, sin(0.1*T)) -0.5);
	vec3 ta = vec3(taAnim, -0.05, 0.0);

    mat3 ca = setCamera(ro, ta, 0.0);
    vec3 rd = ca * normalize(vec3(v, mix(4.5, 5.0, taAnim)));    
    vec3 p, c;
    vec3 n, rl, rr;
    p = vec3(0.0);
    c = env(rd);

    float t, d, a;
    t = d = a = 0.0;
    for(int i=0; i<50; ++i) {
        t+=(d=map(p=ro+rd*t));
        if (d<0.01) {
            break;
        }                
    }

    float depth = length(p-ro);

    if (t<25.0) { // if hit scene

    	n = calcNormal(p);
	    rl = reflect(rd, n);
	    rr = refract(rd, n, .19);

        for(int i=0; i<25; ++i) {
            d = map(p=ro+rd*t);
            a += step(d, 0.008) *0.005;
            t += 0.02;
    	}

        a = exp(-a*25.0);
        c = env(mix(rr, rl, a));
		// c = vec3(a); // uncomment to see absorbtion mask
        c *= mix(vec3(1.4, 1.0, 0.9), vec3(1.0), clamp(0.2*length(p), 0.0, 1.0)); // value        
        c *= mix(vec3(1.5), vec3(.3), clamp(pow(0.1*depth, 2.0), 0.0, 1.0)); // fogging
    }

    c = tonemapping(c);

    depth = 0.01*dot(rd, p-ro); // depth paraller to camera
	fragColor = vec4(c, depth);
}

// Crystal Drops
// by Michal 'spolsh' Klos 2017

#define R iResolution
#define T iTime
#define F gl_FragCoord

vec3 sharpen()
{ // by NickWest (lslGRr)
    vec2 uv = F.xy/R.xy;
  	vec2 step = 1.0/iResolution.xy;
    float scale = 1.5;
	vec3 texA = texture(iChannel0, uv + vec2(-step.x, -step.y) * scale).rgb;
	vec3 texB = texture(iChannel0, uv + vec2( step.x, -step.y) * scale).rgb;
	vec3 texC = texture(iChannel0, uv + vec2(-step.x,  step.y) * scale).rgb;
	vec3 texD = texture(iChannel0, uv + vec2( step.x,  step.y) * scale).rgb;   
    vec3 around = 0.25 *(texA+texB+texC+texD);
	vec3 center = texture(iChannel0, uv).rgb;
	vec3 col = center +(center-around)*1.0;
    return col;
}

float depthToMask(float d)
{
	d *= 100.0;
    d = abs(d -8.5);
    d = pow(d, 6.0);
    d = clamp(d, 0.0, 1.0);    
    return d;
}

vec2 Hash22(vec2 uv)
{
    float f = uv.x + uv.y * 37.0;
    return fract(cos(f)*vec2(10003.579, 37049.7));
}

vec4 bokeh(vec2 uv, float rad)
{ // based on dof by Jochen "Virgill" FeldkÃ¶tter, Alcatraz / Rhodium 4k Intro liquid carbon
  // simplyfied version of Dave Hoskins blur
  // now bokeh is not cut within dof mask, added alpha blending based on difference of dof mask samples
    const float GA =2.399; 
	const mat2 rot = mat2(cos(GA),sin(GA),-sin(GA),cos(GA));
	vec4 acc = vec4(texture(iChannel0,uv).rgb, rad);
    float d = rad;
    vec2 pixel=0.0002*vec2(5.*R.y/R.x,7.);
	vec2 angle=vec2(0,rad);
	for (int j=0;j<80;j++)
    {  
        rad += 1./rad;
	    angle*=rot;
        vec2 tap_uv = uv+pixel*(rad-1.)*angle;
        if (abs(tap_uv.y*2.-1.) > 0.743) continue; // fix letterbox artifacts
        vec4 col=texture(iChannel0, tap_uv);
      	acc.rgb = max(acc.rgb,col.rgb);
        acc.a = max(acc.a, abs(d-depthToMask(col.w)));
	}
	return acc;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{      
    vec2 uv = F.xy/R.xy;    
    vec2 v = 2.0*(uv-0.5);
    v.x *= R.x/R.y;

    float cas = step(abs(v.y)*2.39,R.x/R.y);
    if (cas<0.1) return;

    float d = depthToMask(texture(iChannel0, uv).w);
    // fragColor = vec4(d); return; // uncomment to see dof mask

    vec3 sharp = sharpen();   
	vec4 dof = bokeh(uv, d);
    fragColor = vec4(mix(sharp, dof.rgb, dof.a), 1.0);
}
