vec2 as_polar(vec2 uv, vec2 center)
{
    vec2 pos = vec2(center) - uv;
    float r = length(pos)*2.0;
    float a = atan(pos.y, pos.x);
    return vec2(r, a);
}

float impulse(float x, float k)
{
    float h = k*x;
    return h*exp(1.0-h);
}

vec4 gear(
    vec2 uv,
    vec2 center,
    float rad,
    float speed,
    vec4 gearCol,
    vec4 gearOutlineCol
) {
    vec4 col = vec4(0.0);
    vec2 uvp = as_polar(uv, center);
    float impulsedSpeed = iTime*speed*0.5 + 1.5*sign(speed)*impulse(mod(iTime, 1.), 10.);
    float gearSDF = smoothstep(-0.556,0.8, cos(uvp.y*6. + impulsedSpeed))*0.15+0.5;

    // Determine gear body.
    float gearMask = 1.0 - smoothstep(rad-0.008, rad, gearSDF*uvp.x);

    // Cutout the center hole.
    float holeRad = rad * 0.95;
    float holeMask = 1.0 - smoothstep(holeRad-0.01, holeRad, uvp.x);
    float gearMaskMinHole = gearMask - holeMask; // Cut hole.
    col = mix(col, gearCol, gearMaskMinHole);

    // Determine gear outline.
    float outlineRad = rad + 0.012;
    float gearOutlineMask = abs(
        (1.0 - smoothstep(outlineRad-0.008, outlineRad, gearSDF*uvp.x)) - gearMask
    );
    float holeOutlineRad = holeRad - 0.03;
    float holeOutlineMask = abs(
        (1.0 - smoothstep(holeOutlineRad-0.01, holeOutlineRad, uvp.x)) - holeMask
    );
    col = mix(col, gearOutlineCol, gearOutlineMask + holeOutlineMask);

    //return mix(vec4(0.), gearOutlineCol, gearOutlineMask); // Debug outline..
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float res = clamp(iResolution.y, 300., 600.);
	vec2 uv = (fragCoord - iResolution.xy*.5)/res;
    uv *= 2.0;

    float gearSpeed = 10.;
    float gearRadius = 0.49;
    vec4 col = vec4(0.65, 0.88, 0.88, 1.);
    vec4 gearCol = vec4(0.88, 0.3, 0.3, 1.);
    vec4 gearOutlineCol = vec4(0.3, 0.1, 0.1, 1.);

    vec4 gearOutputCol;

    // Middle row.
    gearOutputCol = gear(uv, vec2(  0.0,  0.0), gearRadius,  gearSpeed, gearCol, gearOutlineCol);
    col = mix(col, gearOutputCol, gearOutputCol.a);

    gearOutputCol = gear(uv, vec2( 1.26,  0.0), gearRadius,  gearSpeed, gearCol, gearOutlineCol);
    col = mix(col, gearOutputCol, gearOutputCol.a);

    gearOutputCol = gear(uv, vec2(-1.26,  0.0), gearRadius,  gearSpeed, gearCol, gearOutlineCol);
    col = mix(col, gearOutputCol, gearOutputCol.a);

    // Top row.
    gearOutputCol = gear(uv, vec2(-1.89, 0.63), gearRadius, -gearSpeed, gearCol, gearOutlineCol);
    col = mix(col, gearOutputCol, gearOutputCol.a);

    gearOutputCol = gear(uv, vec2(-0.63, 0.63), gearRadius, -gearSpeed, gearCol, gearOutlineCol);
    col = mix(col, gearOutputCol, gearOutputCol.a);

    gearOutputCol = gear(uv, vec2( 0.63, 0.63), gearRadius, -gearSpeed, gearCol, gearOutlineCol);
    col = mix(col, gearOutputCol, gearOutputCol.a);

    gearOutputCol = gear(uv, vec2( 1.89, 0.63), gearRadius, -gearSpeed, gearCol, gearOutlineCol);
    col = mix(col, gearOutputCol, gearOutputCol.a);

    // Bottom row.
    gearOutputCol = gear(uv, vec2(-1.89,-0.63), gearRadius, -gearSpeed, gearCol, gearOutlineCol);
    col = mix(col, gearOutputCol, gearOutputCol.a);

    gearOutputCol = gear(uv, vec2(-0.63,-0.63), gearRadius, -gearSpeed, gearCol, gearOutlineCol);
    col = mix(col, gearOutputCol, gearOutputCol.a);

    gearOutputCol = gear(uv, vec2( 0.63,-0.63), gearRadius, -gearSpeed, gearCol, gearOutlineCol);
    col = mix(col, gearOutputCol, gearOutputCol.a);

    gearOutputCol = gear(uv, vec2( 1.89,-0.63), gearRadius, -gearSpeed, gearCol, gearOutlineCol);
    col = mix(col, gearOutputCol, gearOutputCol.a);

    fragColor = col;
}
