float random(vec2 uv)
{
 	return fract(sin(dot(vec2(101, 322.), uv)) * 51214.);
}

float value_noise(vec2 uv)
{
 	vec2 i = floor(uv);
    vec2 f = fract(uv);

    f = f * f * (3. - 2. * f);

    float b = mix(random(i), random(i + vec2(1., 0.)), f.x);
    float t = mix(random(i + vec2(0., 1.)), random(i + vec2(1.)), f.x);

    return mix(b,t,f.y);
}

float noise(vec2 uv)
{
    float n = value_noise(uv);
    n += value_noise(uv * 2.) * 0.5;
    n += value_noise(uv * 4.) * 0.25;
    n += value_noise(uv * 8.) * 0.125;
    n += value_noise(uv * 16.) * 0.0625;

    return n / 1.9375;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 col = mix(vec4(1., 0.1, 0., 1.0), vec4(1., 0.3, 0., 1.), noise(uv * 100.));
    col = mix(vec4(.75, 0.,0.,1.), col * 2., noise(uv * 100.));
    col = mix(col, vec4(2., .4, 0., 1.) * 0.75 ,noise(uv * 7.));
    col = mix( col,vec4(2., .8, 0.2, 1.) * 1.2, noise(uv * 7.));

    // Output to screen
    fragColor = vec4(col);
}
