float gtime;

const float pi = 3.141592653589793;

float paramA, paramB, paramC, paramD;

vec2 beat (float time) {
  if (time<=0.) return vec2(0.);
  vec2 b = vec2(0.);
  for (float i = 1.; i < 10.; i+=0.5) {
    vec4 rnd = texture(iChannel0, vec2(i/10.+gtime*paramB,0.5));
    b += 0.5*sin(rnd.a*paramC+time*paramD*pi*2.*float(i))/float(i)*rnd.xy;
  }
  return b*exp(-time*10.*paramA);
}

vec2 mainSound( in int samp, float time )
{
    time += 0.;
    vec2 v = vec2(0.);
    float k = 100.;
    time *= 1.;
    time -= sin(time/k*pi*2.)*k/pi/2.;
    gtime = time;

    paramA = 0.5+0.5*sin(time*0.07);
    paramB = 0.4*cos(time*0.03);
    paramC = 100.*floor(cos(time*0.05)*10.)/10.;
    paramD = 400.*cos(time*0.02);

    v += 0.25*beat(mod(time,0.25));
    v += 0.25*beat(mod(time+0.75,1.00));
    v += 0.25*beat(mod(time+0.375,2.00));
    v += 0.25*beat(mod(time+0.25,4.00));
    v += 0.25*beat(mod(time+0.125,8.00));
    v += 0.25*beat(mod(time+0.0625,16.00));

    return v;
}

