#define PIX 64. // pixel size (i.e. zoom )

//#define rnd(p) fract(sin((p) * mat2(127.1,311.7,269.5,183.3) )*43758.5453123)
#define rnd(p) fract(sin( (p) * vec2(27.1,311.7) )*43758.5453123)
#define rot(a) mat2(cos( a + vec4(0,33,11,0)))   // From https://www.shadertoy.com/view/XlsyWX
#define hue(v) ( .6 + .6 * cos( 6.3*(v)  + vec4(0,23,21,0)  ) ) // From https://www.shadertoy.com/view/ll2cDc


// --- return hexa cell center + dist to center. // From https://www.shadertoy.com/view/lsVBWw

vec2 Hcoords( vec2 U, out float d ) {
#define SQR3S2 0.866
    U.y /= SQR3S2;
    U.x -= .5*U.y;                           // global slant

    vec2 F = fract(U);                       // triangular barycentric coordinates
    bool u = F.x+F.y > 1.;       
    vec3 q = vec3(F, fract(1.-F.x-F.y));
    if (u) q = 1.-q;
    vec3   r = q - q.zxy;

    U = floor(U);                            // hexagonal center, d = dist to border
    if      ( r.x>0. && r.y<0. ) U += vec2(!u,u), d = min(r.x,-r.y);
    else if ( r.y>0. && r.z<0. ) U += vec2(u,!u), d = min(r.y,-r.z);
    else                         U += vec2(u)   , d = min(r.z,-r.x);

    return U;
}

#define blend(c,d) O = mix(O, vec4(c), smoothstep(.02,0.,d))
#define D(U,d,h)  length( U -d*D -h*H )      // disk at coord(d,h) relative to (D,H)

void mainImage( out vec4 O,  vec2 U )
{
    vec2 R = iResolution.xy, r = 400.*R/R.y / PIX;
    U *= 400./R.y;
    U /= PIX; 
    float d;
    vec2 P = Hcoords(U * rot(-.2), d);
  //O = ( smoothstep(.0,.01,d-.33)*.4 + smoothstep(.0,-.01,d-.23)*.2 )
    O = ( smoothstep(.0,.01,d-.33)*.4 + sqrt(clamp(-5.*(d-.23),0.,1.))*.2 )
         * vec4(.7,.7,1,0)*.6;

    for (float i=0.; i<20.; i++) {           // draw energy balls
        d = length( U - rnd(i)*r+.06*cos(i+4.*iTime+vec2(33,0)) );
        vec2 r = rnd(i+.5);
        O +=  .01*(1.+r.x+.3*sin(6.3*r.y+13.*iTime)) / (d*d) * hue(i/10.);
    }

    vec2 P0 = vec2(330,150)/PIX, D = vec2(10,5)/PIX, H = vec2(-D.y,D.x);
    for (float i=0.; i<15.; i++) {           // draw worm body
        float t = 10.*(iTime-10.);
        P = mod( P0 + (i+t) * D + .3*H*sin(i/2.+t), r+.39/1.41 );
        d = length( U - P );
        blend(d, d-.37);
    }
    d =       D( U-P, 1., .7 );              // draw worm eyes
    d = min(d,D( U-P, 1.,-.7 ));
    blend(1., d-.15);

    d =       D( U-P, 1.4, .7 );
    d = min(d,D( U-P, 1.4,-.7 ));
    blend(0., d-.08);
}
