// variant of https://shadertoy.com/view/4syBWd

void mainImage(out vec4 O, vec2 U) {

    float t = iTime, l,a,z,i, r=1., h=6.;        // r: radius h: height/2.
    mat2  R = mat2( sin(t+vec4(0,33,11,0)) );
    vec3  q = iResolution,
          D = normalize(vec3(.3*(U+U-q.xy)/q.y, -1)),  // ray direction
          p = 30./q, q0;                         // marching point along ray

    O-=O;
    for ( O++; O.x > 0. && t > .01 ; O-=.015 ) {
        q = p,
        q.xz *= R, q.yz *= R,                    // rotation
        i = 2. + floor(abs(q.x/6.));             // column id
        q0 = q; z = q.z;
        q.x = mod(q.x,6.)-3.,                    // repeat in x
        q.y = abs(q.y)-5.;                       // 2 rows
        q.z = mod(q.z,3.) - 1.5,                 // vertical blocks

        a = atan(q.y,q.x) -z, l = length(q.xy);
        t = length(vec2(l-r,(mod(i*a,6.28)-3.14)*1./i))-r*.3; // crown of columns
        if (q0.y>0.)
            a += 2.*z,                           // waffle: max of inv twist
            t = min(t, length(vec2(l-r,(mod(i*a,6.28)-3.14)*1./i))-r*.3 );
        t = max(t,abs(z) -h),                    // inter range of Z
        t = min(t, max(l-r*1.3,abs(abs(z)-h)-.2));

        p += t*D;                                // step forward = dist to obj
    }
}
