vec3 makeColor256(int r, int g, int b)
{
    return vec3(float(r)/255.0, float(g)/255.0, float(b)/255.0);
}


vec3 lerp(float mix, vec3 a, vec3 b)
{
    return mix * a + (1.0 - mix) * b;
}


float wrap(float x, float xmin, float xmax)
{
    return 0.0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{     
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/max(iResolution.x, iResolution.y);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec4 perlin = texture(iChannel0, 0.25 * uv);

    float numCycles = 8.0 * 3.14159;
    float size = (0.5 * sin(iTime) + 0.5) * 16.0 + 8.0;
    float numCyclesY = numCycles;
    float sizeY = size;

    vec3 color1 = makeColor256(190, 190, 161);
    vec3 color2 = makeColor256(60, 89, 60);

    float marble = sin(numCycles * uv.x + size * perlin.x) * sin(numCyclesY * uv.x + sizeY * perlin.y);
    col = lerp(0.5 * marble + 0.5, color1, color2);

    // Output to screen
    fragColor = vec4(col,1.0);// * perlin;
}
