void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord / iResolution.y, s = vec2(.0),c = vec2(.0);
    vec2 vv = fragCoord / iResolution.xy;
    vec4 pix = texture(iChannel0,vv);
    vec3 pixel = vec3(1.2);
    float t = iTime * 0.037,d = 0.0,r = 0.0;
	for(float i=0.0; i<37.0; i+=1.0) {
       s = vec2(t + fract(sin(i*0.9)*37.0),t + fract(cos(i*2.5)*37.0));
       c.x = fract(cos(i*42.9 + s.x)*2.551) * 4.0 - 1.0;
       c.y = fract(sin(i*13.44 + s.y)*0.987) * 3.0 - 1.0;
       r = fract(sin((i*0.6))*5.0) * 0.67;
       c += pix.xy*vec2(0.3,0.1);
       d = length(uv - c);
       pixel[int( mod(i,3.0))] -= smoothstep(d*0.05,d,r*r)*0.67;
    }

    fragColor = vec4(pixel.rgb, 1.0);
}

void mainImage( out vec4 O, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;
    float t = iTime * 1.1;
    t += fract(sin(uv.x+1.0))*2.0;
    float e = 1.0+sin(uv.x*3.0)*2.6;
    float k =  cos(t-e) + 7.0;
    O = vec4( abs( cos(t + sin(0.5 * t + (uv.x+t*0.001) * k) ) ));
}
