  #define rnd(p) fract(sin(p * mat2(127.1,311.7,269.5,183.3) )*43758.5453123)

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    U = (U+U-R)/R.y;
    O -= O;

    float t = -iTime, v = 3., T = 5.,                    // rotation
          a = t + length(U);
          a = - cos( mod( a/T ,3.1415926) ) * T;
    U *= mat2(cos( v*a + vec4(0,33,11,0)));

    if (U.x<0.) U = -U;                                  // quadrant symmetry
    if (U.y>0.) U = vec2(U.y,-U.x);

    U.x -= .3;
    U.x /= U.y/4.; // / (4.-3.*abs(a/T)); 
    if (U.y > 0. || abs(U.x)>1. ) return;                // jet cone

    U.y -= t;                                            // jet
    O = ( 2. - length( fract(U)-rnd(floor(U*1e2)/1e2) ) / fwidth(U.x) )
        * vec4(1,.8,.6,0);
    //  ( 1.2 - .2*  
}
