//Ethan Alexander Shulman 2017


#define POINT_INTENSITY 0.1
#define POINT_FADE 0.8
#define ITERATIONS 128

#define NUM_POINTS ITERATIONS*FRAMES



//function outputs point position, radius and color from point id i
void f(int i, out vec2 p, out float r, out vec3 c) {
	float snd = texture(iChannel1, vec2(float(i)/float(ITERATIONS), 0.2)).x;
    float fi = snd*snd*2.9783+(float(i)/float(ITERATIONS))*6.28;

    p = vec2(sin(fi),cos(fi))*cos(snd*4.98+iTime*0.3)*0.4+0.5;
	r = 0.05;
    c = (vec3(sin(fi*0.477),cos(fi*0.538+iTime*0.6),-cos(fi*0.37))*0.5+0.5)*snd;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (iFrame < 60) {
        //init
        fragColor = vec4(0.);
        return;
    }

    vec2 uv = fragCoord/iResolution.xy;
    vec4 c = texture(iChannel0, uv)*POINT_FADE;

	//render points
    for (int i = 0; i < ITERATIONS; i++) {
        vec2 p;
        vec3 l;
        float r;
        f(i, p, r, l);
        c.xyz += l*POINT_INTENSITY*max(0.,1.-length(uv-p)/r);
    }

    fragColor = c;
}
