// creates a repeating gradient texture for use elsewhere

vec3 color1 = vec3(0.2, 0.8, 0.2);
vec3 color2 = vec3(0.0, 0.0, 0.0);
vec3 color3 = vec3(1.0, 1.0, 1.0);
float stops = 3.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // get coords
    vec2 uv = fragCoord/iResolution.xy;

    // make it wave, just for fun
    uv.y += 0.1 + (0.1 * sin(iTime)) * sin(uv.x * (6.28 + 3.14));

    // animate to test repeat
    uv.y *= 10.;			// zoom
    uv.y -= iTime / 3.14;	// scroll loop
    uv = fract(uv);			// repeat

    // fade colors
    float stopSize = 1. / stops;
    vec3 col = vec3(0.);
    if(uv.y < stopSize) {
    	col = mix(color1, color2, smoothstep(0.15, 0.85, uv.y * stops)); 
    } else if(uv.y < stopSize * 2.) {
    	col = mix(color2, color3, smoothstep(0.15, 0.85, (uv.y - stopSize) * stops)); 
    } else {
    	col = mix(color3, color1, smoothstep(0.15, 0.85, (uv.y - stopSize * 2.) * stops)); 
    }

    // draw
    fragColor = vec4(col,1.0);
}
