float PrEllipsDf (vec3 p, vec3 r);
float PrEllCylDf (vec3 p, vec2 r, float h);
float SmoothBump (float lo, float hi, float w, float x);
float SmoothMin (float a, float b, float r);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
vec3 HsvToRgb (vec3 c);
float Hashfv2 (vec2 p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 qHit, sunDir, dukConf;
vec2 gId;
float dstFar, tCur, szFac;
const float pi = 3.14159;
int idObj;
const int idBdy = 1, idWng = 2, idHead = 3, idBk = 4;

vec3 SkyCol (vec3 ro, vec3 rd)
{
  float f;
  ro.xz += 0.5 * tCur;
  f = Fbm2 (0.1 * (ro + rd * (50. - ro.y) / rd.y).xz);
  return mix (vec3 (0.2, 0.3, 0.55) + 0.25 * pow (1. - max (rd.y, 0.), 8.) +
     0.35 * pow (max (dot (rd, sunDir), 0.), 16.),
     vec3 (0.85), clamp (0.8 * f * rd.y + 0.1, 0., 1.));
}

float WaveHt (vec2 p)
{
  vec2 u;
  u = mod (0.1 * vec2 (p.x + p.y, p.x - p.y) * (1. + 0.3 * sin (0.2 * 2. * pi * p)) +
   vec2 (-0.1, 0.053) * tCur, 1.) - 0.5;
  u = exp (-100. * u * u);
  return 0.5 + (u.x + u.y) / (3. + 0.05 * dot (p, p));
}

float WaveRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, h, s, sLo, sHi;
  s = 0.;
  sLo = 0.;
  dHit = dstFar;
  for (int j = 0; j < 50; j ++) {
    p = ro + s * rd;
    h = p.y - WaveHt (p.xz);
    if (h < 0.) break;
    sLo = s;
    s += max (0.5, h);
    if (s > dstFar) break;
  }
  if (h < 0.) {
    sHi = s;
    for (int j = 0; j < 8; j ++) {
      s = 0.5 * (sLo + sHi);
      p = ro + s * rd;
      h = step (0., p.y - WaveHt (p.xz));
      sLo += h * (s - sLo);
      sHi += (1. - h) * (s - sHi);
    }
    dHit = sHi;
  }
  return dHit;
}

vec3 WaveNf (vec3 p)
{
  vec2 e = vec2 (0.01, 0.);
  float h;
  h = WaveHt (p.xz);
  return normalize (vec3 (h - WaveHt (p.xz + e.xy), e.x, h - WaveHt (p.xz + e.yx)));
}

float DukDf (vec3 p)
{
  vec3 q;
  vec2 r, cs;
  float dMin, d, h, s;
  dMin = dstFar / szFac;
  p /= szFac;
  q = p;
  r = vec2 (0.04, 0.06 + 0.01 * clamp (q.z, -0.4, 0.4));
  h = 0.1;
  s = (length (q.xz / r) - 1.) * min (r.x, r.y);
  d = min (max (s, abs (q.y) - h), length (vec2 (s, q.y)) - h);
  if (d < dMin) { dMin = d;  idObj = idBdy;  qHit = q; }
  q = p;
  q.x = abs (q.x);
  q -= vec3 (0.1, 0.06, 0.12);
  cs = vec2 (cos (0.3), sin (0.3));
  q.yz = Rot2Cs (q.yz, cs);
  cs.y = - cs.y;
  q.xy = Rot2Cs (q.xy, cs);
  q.xz = Rot2Cs (q.xz, cs);
  q = q.yxz;
  r = vec2 (0.06, 0.1 + 0.016 * clamp (q.z, -0.4, 0.4));
  h = 0.014;
  s = (length (q.xz / r) - 1.) * min (r.x, r.y);
  d = min (max (s, abs (q.y) - h), length (vec2 (s, q.y)) - h);
  d = SmoothMin (d, dMin, 0.01);
  if (d < dMin) { dMin = d;  idObj = idWng;  qHit = q; }
  q = p;
  q.yz -= vec2 (0.15, -0.08);
  d = PrEllipsDf (q, vec3 (0.08, 0.07, 0.1));
  d = SmoothMin (d, dMin, 0.02);
  if (d < dMin) { dMin = d;  idObj = idHead;  qHit = q; }
  q = p;
  q.yz -= vec2 (0.14, -0.19);
  r = vec2 (0.03, 0.008);
  h = 0.02;
  d = max (PrEllCylDf (q, r, h), - PrEllCylDf (q - vec3 (0., 0., h),
     r - 0.004, 2. * h));
  if (d < dMin) { dMin = d;  idObj = idBk;  qHit = q; }
  return 0.9 * dMin * szFac;
}

#define SQRT3 1.7320508

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./SQRT3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (SQRT3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

float ObjDf (vec3 p)
{
  p.xz -= HexToPix (gId);
  p.y -= 0.02 * szFac + dukConf.x;
  p.yz = Rot2D (p.yz, -3. * dukConf.z);
  p.xy = Rot2D (p.xy, 4. * dukConf.y);
  return DukDf (p);
}

void EvalDukConf ()
{
  vec4 ht;
  vec2 e, g;
  szFac = 3. - 0.3 * floor (5. * Hashfv2 (17. * gId));
  e = vec2 (0.03 * szFac, 0.);
  g = HexToPix (gId);
  ht = vec4 (WaveHt (g - e), WaveHt (g + e), WaveHt (g - e.yx), WaveHt (g + e.yx));
  dukConf = vec3 (0.25 * dot (ht, vec4 (1.)) + 0.1 * (Hashfv2 (22. * gId) - 0.5),
     vec2 (ht.xz - ht.yw));
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 vri, vf, hv, p;
  vec2 edN[3], pM, gIdP;
  float dHit, d, s;
  edN[0] = vec2 (1., 0.);
  edN[1] = 0.5 * vec2 (1., SQRT3);
  edN[2] = 0.5 * vec2 (1., - SQRT3);
  for (int k = 0; k < 3; k ++) edN[k] *= sign (dot (edN[k], rd.xz));
  vri = 1. / vec3 (dot (rd.xz, edN[0]), dot (rd.xz, edN[1]), dot (rd.xz, edN[2]));
  vf = (SQRT3/2.) - vec3 (dot (ro.xz, edN[0]), dot (ro.xz, edN[1]),
     dot (ro.xz, edN[2]));
  pM = HexToPix (PixToHex (ro.xz));
  gIdP = vec2 (-10.);
  dHit = 0.;
  for (int j = 0; j < 150; j ++) {
    hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
    s = min (hv.x, min (hv.y, hv.z));
    p = ro + dHit * rd;
    gId = PixToHex (p.xz);
    if (max (max (abs (gId.x), abs (gId.y)), abs (gId.x + gId.y)) <= 4.) {
      if (gId.x != gIdP.x || gId.y != gIdP.y) {
        EvalDukConf ();
        gIdP = gId;
      }
      d = ObjDf (p);
    } else d = dstFar;
    if (dHit + d < s) {
      dHit += d;
    } else {
      dHit = s + 0.01;
      pM += SQRT3 * ((s == hv.x) ? edN[0] : ((s == hv.y) ? edN[1] : edN[2]));
    }
    if (d < 0.001 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec3 e = vec3 (0.001, -0.001, 0.);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy),
     ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.01;
  for (int j = 0; j < 20; j ++) {
    h = ObjDf (ro + rd * d);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += 0.015;
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

vec3 ObjCol ()
{
  vec3 col, cBdy;
  float s;
  cBdy = HsvToRgb (vec3 (mod (Hashfv2 (gId * vec2 (17., 27.) + 0.5), 1.), 1., 1.));
  if (idObj == idBdy) {
    col = cBdy * (1. - smoothstep (0.02, 0.06, qHit.y) *
       smoothstep (0., 0.14, qHit.z) * 0.1 * SmoothBump (0.3, 0.5, 0.05,
       mod (50. * qHit.x, 1.)));
  } else if (idObj == idWng) {
    col = cBdy * (1. - step (0.004, qHit.y) *
       smoothstep (0., 0.04, qHit.z) * 0.2 * SmoothBump (0.3, 0.5, 0.05,
       mod (100. * qHit.x, 1.)));
  } else if (idObj == idHead) {
    s = length (qHit.yz - vec2 (0.02, -0.05));
    if (s > 0.02) col = cBdy;
    else col = (abs (s - 0.01) < 0.003) ? vec3 (1., 1., 1.) : vec3 (0.3, 0.3, 1.);
  } else if (idObj == idBk) col = vec3 (1., 0.5, 0.);
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, colW, vn;
  float dstObj, dstWat, sh;
  bool waterRefl;
  dstWat = WaveRay (ro, rd);
  dstObj = ObjRay (ro, rd);
  waterRefl = (dstWat < min (dstFar, dstObj));
  if (waterRefl) {
    ro += rd * dstWat;
    vn = WaveNf (ro);
    vn = VaryNf (10. * ro, vn, 0.3);
    colW = 0.7 * vec3 (0., 0.2, 0.3) * (0.4 + 0.6 * max (dot (vn, sunDir), 0.));
    rd = reflect (rd, vn);
    dstObj = ObjRay (ro, rd);
  }
  if (dstObj < dstFar) {
    ro += rd * dstObj;
    gId = PixToHex (ro.xz);
    col = ObjCol ();
    EvalDukConf ();
    vn = ObjNf (ro);
    sh = ObjSShadow (ro, sunDir);
    col = col * (0.2 + 0.8 * sh * max (dot (vn, sunDir), 0.)) +
       0.2 * sh * pow (max (dot (normalize (sunDir - rd), vn), 0.), 128.);
  } else col = SkyCol (ro, rd);
  if (waterRefl) col = mix (colW, col, 0.3);
  col = pow (clamp (col, 0., 1.), vec3 (0.7));
  return col;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd;
  vec2 canvas, uv, ori, ca, sa;
  float az, el;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  if (mPtr.z > 0.) {
    az = 2. * pi * mPtr.x;
    el = 1. * pi * mPtr.y;
  } else {
    az = -0.03 * pi * tCur;
    el = -0.2 * pi + 0.05 * pi * sin (0.1 * pi * tCur);
  }
  el = clamp (el, -0.45 * pi, -0.12 * pi);
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
     mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0., 0., -12.);
  rd = vuMat * normalize (vec3 (uv, 4.));
  dstFar = 40.;
  sunDir = vuMat * normalize (vec3 (1., 2., -1.));
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrEllipsDf (vec3 p, vec3 r)
{
  return (length (p / r) - 1.) * min (r.x, min (r.y, r.z));
}

float PrEllCylDf (vec3 p, vec2 r, float h)
{
  return max ((length (p.xy / r) - 1.) * min (r.x, r.y), abs (p.z) - h);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 HsvToRgb (vec3 c)
{
  vec3 p;
  p = abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.);
  return c.z * mix (vec3 (1.), clamp (p - 1., 0., 1.), c.y);
}

const vec4 cHashA4 = vec4 (0., 1., 57., 58.);
const vec3 cHashA3 = vec3 (1., 57., 113.);
const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, cHashA3.xy)) * cHashM);
}

vec4 Hashv4f (float p)
{
  return fract (sin (p + cHashA4) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec4 t;
  vec2 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv4f (dot (ip, cHashA3.xy));
  return mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f;
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 3; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  float s;
  const vec3 e = vec3 (0.1, 0., 0.);
  s = Fbmn (p, n);
  g = vec3 (Fbmn (p + e.xyy, n) - s, Fbmn (p + e.yxy, n) - s,
     Fbmn (p + e.yyx, n) - s);
  return normalize (n + f * (g - n * dot (n, g)));
}
