#define PI 3.14159

const float POINT_SIZE = 0.09;
const float BRIGHTNESS = 0.3;
const int LIGHT_CNT = 20;

float random(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}


///point(global time, uv, center of the point, xScale, yScale,deerection, size )
float point(float time, vec2 _uv, vec2 pos, float xs, float ys, vec2 dir, float size)
{
   float hypo = distance(vec2(pos.x + mod(time,2.0)* dir.x *xs,
                              pos.y + mod(time,2.0)* dir.y *ys),_uv);

   return size / pow(length (hypo),1.8);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy-iResolution.xy*0.5) / iResolution.xy;    
    uv *= 2.0;

    float col = 0.0;

    for(int i=0; i<LIGHT_CNT;i++)
        {
        col += point(iTime,uv,vec2(sin(iTime + float(i)*0.5)*2.0,4.0), 0.5,4.0,vec2(0.0,-0.8),POINT_SIZE);            
        }        
    col *= BRIGHTNESS;


	fragColor = vec4(1.0*col,1.0*col,1.0*col,1.0);
}
