// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

/*
   Cardioid facts:
  ----------------
	- (a) Trace a point on the circle rolling around another circle of equal radius.
	- (b) There are exactly three parallel tangents at any given point on the cardioid.
	- (c) The tangents at the ends of any chord through the cusp point are at right angles.
*/

#define res_            iResolution
#define time_           iTime
#define trace_len_      64
#define eps_            0.1

vec2  domain(vec2 uv, float s);
vec2  parametric(float t);
float trace(vec2 p, float t);

void mainImage(out vec4 o, vec2 f) {
	vec2 p = domain(f, 8.);

    // t -> [0., 5.]
    float t = abs(sin(time_)*5.);

    // Cardioid traces
    float dtrace = smoothstep(0.08, 0.001, trace(p, t));
    float dtrace_s0 = smoothstep(0.16, 0.001, trace(p, t));

    // Axis
    float ax = min(
        smoothstep(0., 0.02, abs(p.x)),
        smoothstep(0., 0.02, abs(p.y))
    );
    // Origin
    float org = smoothstep(0.09, 0., length(p)-.01);

    // Palette 
    vec3 gray   = vec3(.2);
    vec3 white  = vec3(5.);

    // Shading
    vec3 c  = vec3(0.);
    c = mix(c, gray, 1.-ax);
    c = mix(c, white, org);

    c = mix(c, vec3(11., 0.0, 0.), dtrace_s0);
    c = mix(c, white, dtrace);
    c.b += dtrace_s0;

    o = vec4(sqrt(c), 1.);
}

vec2 parametric(float t) {
	float a = 2.;
    return vec2(a*( 2.*sin(t) - sin(2.*t) ), a*( 2.*cos(t)-cos(2.*t) ) ); 
}

float trace(vec2 p, float t) {
    float d = 1e10;
    float e = 0.;
    for(int i = 0; i < trace_len_; i++) {
    	d  = min(d, distance(p, parametric(t-e)));
        e += eps_; 
    }
    return d;
}

vec2 domain(vec2 uv, float s) {
    return (2. * uv.xy - res_.xy) / res_.y * s;
}
