float random (in float x) {
    return fract(sin(x)*1e4);
} 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 st = fragCoord.xy / iResolution.xy;
    vec2 mouseRatio = iMouse.xy / (iResolution.xy*2.0);
    //float angle = radians(mod(iTime,360.0));
    //vec2 mouseRatio = vec2(cos(iTime*0.5),sin(iTime*0.5));
    vec3 bgcolor = vec3(248.0/255.0,241.0/255.0,225.0/255.0);
    vec3 red = vec3(168.0/255.0,34.0/255.0,35.0/255.0);
    vec3 yellow = vec3(254.0/255.0,195.0/255.0,45.0/255.0);
    vec3 blue = vec3(0.0,93.0/255.0,151.0/255.0);
    vec3 color = bgcolor;

    float lineW = 0.01;
    float lineH = lineW*iResolution.x/iResolution.y;
    float powValue = 1.89;
    float divider = 4.0;
    vec2 start = mouseRatio;


    float hiddenLine = floor(mod(iTime,divider));

    for (float j=0.0;j<divider;j+=1.0) {
        if (j>=hiddenLine && j<hiddenLine+0.5) {
            float startY = pow(start.y+mouseRatio.y*(j-1.0),powValue);
            float colorY1 = startY;
            float colorY2 = pow(start.y+mouseRatio.y*(j+1.0),powValue);
            if (j>0.0) {
                colorY1 += lineH;
            }
            if (j>divider-1.5) {
                colorY2 = 1.0;
            }
            if (st.y>=colorY1 && st.y<=colorY2 && st.x<pow(start.x, powValue)) {
                color=red;
            }
        }
    }

    for (float i=0.0;i<divider;i+=1.0) {
        float startX = pow(start.x+mouseRatio.x*i,powValue);
        if (st.x>startX && st.x<startX+lineW) {
            color=vec3(0.0);
        }
    }
	for (float j=0.0;j<divider;j+=1.0) {
        float startY = pow(start.y+mouseRatio.y*j,powValue);
        if (st.y>startY && st.y<startY+lineH) {
            color=vec3(0.0);
        }
        if (j>=hiddenLine && j<hiddenLine+0.5) {
            float startX = pow(start.x*(divider-1.0),powValue)+lineW;
            float endX = pow(start.x*(divider),powValue);
            if (st.x>startX && st.x<endX) {
                if (st.y>startY && st.y<startY+lineH) {
                	color=bgcolor;
                }
                float colorY1 =pow(start.y+mouseRatio.y*(j-1.0),powValue);
                float colorY2 = pow(start.y+mouseRatio.y*(j+1.0),powValue);
                if (j>0.0) {
                	colorY1 += lineH;
            	}
                if (j>divider-1.5) {
                	colorY2 = 1.0;
            	}
                if (st.y>colorY1 && st.y<colorY2) {
                	color=yellow;
            	}
            }
        }
    }

    for (float i=0.0;i<divider;i+=1.0) {
        if (i>=hiddenLine && i<hiddenLine+0.5) {
            float startX = pow(start.x+mouseRatio.x*(i-1.0),powValue);
            float colorX1 = startX;
            float colorX2 = pow(start.x+mouseRatio.x*(i+1.0),powValue);
            if (i>0.0) {
                colorX1 += lineW;
            }
            if (i>divider-1.5) {
                colorX2 = 1.0;
            }
            if (st.x>=colorX1 && st.x<=colorX2 && st.y<pow(start.y, powValue)) {
                color=blue;
            }
        }
    }

	fragColor = vec4(color,1.0);
}
