// variant of https://www.shadertoy.com/view/llK3Wt

void mainImage( out vec4 O,  vec2 U )
{
    O = mix (vec4(0,0,1,1), vec4(1), 1.2*texture(iChannel0, U/iResolution.xy) );
}

#define T(x,y) texture( iChannel0, fract((U+vec2(x,y))/R) )

void mainImage( out vec4 O,  vec2 U )
{
    vec2 R = iResolution.xy;

    if ( iFrame < 100 || U.y/R.y > .98 ) {      // init : random heights (e.g., rain)
        O = texture( iChannel1, (.1*U+777.*iTime)/iChannelResolution[1].xy ).rrrr;  // .x = h in mm
     // O = .3 + .7*O;
        return;
    }

    O-=O;
    for (float i = -10.; i<=10.; i++)
        O += T(i,0) * (.5+.5*cos(i*.3))/10. ;  // average h a bit laterally ( linear fetch do some vertical average as well)
    float v = sqrt(9.81*O.x/1e3);              // v = sqrt(g.h)   in m/s 
    // or via de ChÃ©zy formula v = C.sqrt(h.slope) ; C = h^1/6 / n ; n =  Manning's roughness coefficient ~ 0.01 to 0.03
    O = T(0,2e3*v/60.);                        // h(x,t+dt) = h(x-v.dt,t)   ( why 2e3 instead of R.y ? )
    // Note that Eulerian + pull tends to overblur the phenomena
}
