// Based on the shader Ellipse - Distance of iq : https://www.shadertoy.com/view/4sS3zz

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (fragCoord*2.-iResolution.xy)/iResolution.y*3.;

    float t = sin(iTime*0.2)*.5+.5;

    float n = 8.;
    float an = 3.14159 / n;
	float a = atan( p.y, p.x );
	a = mod( a, an * 2.0 ) - an;
	p = vec2( cos( a ), sin( a ) ) * length(p);

    p.x -= 2.;

    float rot = iTime;

    p *= mat2(cos(rot),-sin(rot),sin(rot),cos(rot));

    float d = 0.;

    //d = length(p)*.3; 					// circle
    d = max(abs(p.x),abs(p.y));				// quad
   	//d = max(abs(p.y*.87)-p.x*.5,+p.x);	// triangle

    //d = abs(d-.5); 						// add thickness

    d -= 0.5;								// radius

    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-2.0*abs(d));
	col *= 0.8 + 0.2*cos(120.0*abs(d)+iTime*10.);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.02,abs(d)) );

	fragColor = vec4( col, 1.0 );;
}
