float sdPlane( vec3 p )
{
	return p.y;
}
float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0))-r;
}
float sdHexPrism( vec3 p, vec2 h )
{
    vec3 q = abs(p);
    return max(q.z-h.y,max((q.x*0.866025+q.y*0.5),q.y)-h.x);
}
float sdSphere( vec3 p, float s )
{
    return length(p)-s;
}
mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}
float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}
float blend (float a, float b, float k) {
    float h = clamp(0.5+0.5*(b-a)/k, 0.0, 1.0);
    return mix(b,a,h) - k*h*(1.0-h);
}
vec2 blend( vec2 a, vec2 b,float k)
{
    vec2 l = vec2(k);
    return vec2(blend(a.x,b.x,l.x),blend(a.y,b.y,l.y));
}
float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - r;
}
float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

vec2 opU( vec2 d1, vec2 d2 )
{
	return (d1.x<d2.x) ? d1 : d2;
}
vec3 opRep( vec3 p, vec3 c )
{
    return mod(p,c)-0.5*c;
}
float scDir(float p, float s)
{
    vec3 x = vec3(p);
    return length(cross(sin(x), cos(x))-cross(asin(x),acos(x)))-s;
}
float snDir(float p, float s)
{
    vec3 x = normalize(vec3(p));
    vec2 y = vec2(p);
    return scDir(length(x)-s, s);
}
vec2 map( vec3 pos)
{
    float roundness = .15;
  	float rot = 1.;
	float k = .05;
    float size = .5;

    float scale = 1. / ( 3. - clamp( pos.y, 1., 7. ) );
    scale = 1.;

    vec2 x;
    float h = 3.5;
    vec3 p = vec3(pos.x, pos.y, pos.z);
    float d1;
    float pl = sdPlane(p);
    x = vec2(sdPlane(p),1.0);
    for(float i = 1.; i < 10.; i++)
    {
        p = vec3(pos.x+sin(i/5.), pos.y-i/10., pos.z+3.+cos(i/5.)); // Position: Add For Negative, Subtract For Positive.
        p.xz *= rotate2d(i/5.);
        d1 = sdBox(p, vec3(0.75, 0.2, 0.9));
        x = opU(vec2(d1, 5.0), x);
    }
    p = vec3(pos.x, pos.y-.6, pos.z+3.); // Position: Add For Negative, Subtract For Positive.
    d1 = sdCapsule(p, vec3(.5, -1., 0.0), vec3(.5, 1., 0.), 0.5);
    x = blend(vec2(d1, 1.5), x, 0.1);
    p = vec3(pos.x-1., pos.y, pos.z+1.);
    d1 = sdBox(p, vec3(0.3, clamp(p.y, 0.0, 3.0), .3));
    x = opU(vec2(d1, 1.7), x);
    p = vec3(pos.x-1., pos.y, pos.z-5.);
    d1 = sdBox(p, vec3(0.3, clamp(p.y, 0.0, 3.0), .3));
    x = opU(vec2(d1, 1.7), x);
    p = vec3(pos.x+3., pos.y, pos.z+1.);
    d1 = sdBox(p, vec3(0.3, clamp(p.y, 0.0, 3.0), .3));
    x = opU(vec2(d1, 1.7), x);
    p = vec3(pos.x+3., pos.y, pos.z-5.);
    d1 = sdBox(p, vec3(0.3, clamp(p.y, 0.0, 3.0), .3));
    x = opU(vec2(d1, 1.7), x);
    p = vec3(pos.x, pos.y, pos.z+1.);
    d1 = sdBox(p, vec3(clamp(p.x, 2., 3.)+3., clamp(p.y, 0.79, 0.799), clamp(p.z, 1.9, 6.9)));
    x = opU(vec2(d1, 1.5), x);
    p = vec3(pos.x, pos.y-4.3, pos.z+1.);
    d1 = sdBox(p, vec3(clamp(p.x, 2., 3.6)+3., clamp(p.y, 1.21, 1.219), clamp(p.z, 1.9, 6.9)));
    x = opU(vec2(d1, 1.6), x);
    p = vec3(pos.x, pos.y-3.3, pos.z);
    d1 = sdBox(p, vec3(clamp(p.x,0., 0.6), clamp(p.y, 0.71, 0.719), clamp(p.z, 0.45, 0.7)));
    x = opU(vec2(d1, 1.1), x);
    return x;

}
vec2 castRay( in vec3 ro, in vec3 rd )
{
    float tmin = 1.0;
    float tmax = 20.0;

#if 0
    float tp1 = (0.0-ro.y)/rd.y; if( tp1>0.0 ) tmax = min( tmax, tp1 );
    float tp2 = (1.6-ro.y)/rd.y; if( tp2>0.0 ) { if( ro.y>1.6 ) tmin = max( tmin, tp2 );
                                                 else           tmax = min( tmax, tp2 ); }
#endif

	float precis = 0.002;
    float t = tmin;
    float m = -1.0;
    for( int i=0; i<50; i++ )
    {
	    vec2 res = map( ro+rd*t );
        if( res.x<precis || t>tmax ) break;
        t += res.x;
	    m = res.y;
    }

    if( t>tmax ) m=-1.0;
    return vec2( t, m );
}
float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i<16; i++ )
    {
		float h = map( ro + rd*t ).x;
        res = min( res, 8.0*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( h<0.001 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );

}

vec3 calcNormal( in vec3 pos )
{
	vec3 eps = vec3( 0.001, 0.0, 0. );
	vec3 nor = vec3(
	    map(pos+eps.xyy).x - map(pos-eps.xyy).x,
	    map(pos+eps.yxy).x - map(pos-eps.yxy).x,
	    map(pos+eps.yyx).x - map(pos-eps.yyx).x );
	return normalize(nor);
}
float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );    
}
float hash(vec2 p)
{
    return fract(sin(dot(p, vec2(127.1,311.7)))*43758.5453123);
}
float noise(vec2 x)
{
    vec2 p = floor(x);
    vec2 f = fract(x);
    vec2 i = f*f*(3.0-2.0*f);
    return mix(mix(hash(i+vec2(0.0, 0.0)),
                   hash(i+vec2(0.0, 1.0)), x.x),
               mix(hash(i+vec2(1.0, 0.0)),
                   hash(i+vec2(1.0, 1.0)), x.x),x.y);



}
vec3 render( in vec3 ro, in vec3 rd )
{ 
    vec3 col = vec3(0.7, 0.9, 1.0) + sin(rd.y);
    vec2 res = castRay(ro,rd);
    float t = res.x;
	float m = res.y;
    if( m>-0.5 )
    {
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal( pos );
        vec3 ref = reflect( rd, nor );

        // material        
		col = 0.45 + 0.3*sin( vec3(1.0)*(m-1.0) );

        if( m==1.5 )
        {

            float f = 1.;
            col = 0.4 + 0.1*f*vec3(1.);
        }
        else if( m==1.6 )
        {

            float f = 1.;
            col = 0.4 + 0.1*f*vec3(1.0);
        }
        else if( m==1.7 )
        {

            float f = 1.;
            col = texture(iChannel0, vec2(pos.x, pos.z)).xyz*vec3(0.3);
        }
        else if( m==1.0 )
        {

            float f = 1.;
            col = texture(iChannel1, vec2(pos.x, pos.z)).xyz*vec3(0.5, 1.0, 0.5);
        }
        if( m==1.1 )
        {
            col= 0.4+0.1*vec3(1.)+texture(iChannel2, vec2(pos.x,pos.z)).rgb+
                (vec3(0.9, 0.5, 0.1)-clamp(sin(iTime/24.), -0.8, 1.9));
        }

        // lighitng
        vec3  lig = normalize( vec3(-0.6+cos(iTime/24.), 0.7+sin(iTime/24.), -0.5) );
        float occ = calcAO( pos, nor );
		float amb = clamp( 0.5+0.5*nor.y, 0.0, 1.0 );
        float dif = clamp( dot( nor, lig ), 0.0, 1.0 );
        float bac = clamp( dot( nor, normalize(vec3(-lig.x,0.0,-lig.z))), 0.0, 1.0 )*clamp( 1.0-pos.y,0.0,1.0);
        float dom = smoothstep( -0.1, 0.1, ref.y );
        float fre = pow( clamp(1.0+dot(nor,rd),0.0,1.0), 2.0 );
		float spe = pow(clamp( dot( ref, lig ), 0.0, 1.0 ),16.0);

        dif *= softshadow( pos, lig, 0.02, 2.5);
        dom *= softshadow( pos, ref, 0.2, 2.5 );

		vec3 lin = vec3(0.0);
        lin += 1.20*dif*vec3(1.00,0.85,0.55);
		lin += 1.20*spe*vec3(1.00,0.85,0.55)*dif;
        lin += 0.20*amb*vec3(0.50,0.70,1.00)*occ;
        lin += 0.30*dom*vec3(0.50,0.70,1.00)*occ;
        lin += 0.30*bac*vec3(0.25,0.25,0.25)*occ;
        lin += 0.40*fre*vec3(1.00,1.00,1.00)*occ;
		col = col*lin;

    	col = mix( col, vec3(0.8,0.9,1.0), 1.0-exp( -0.002*t*t ) );

    }

	return vec3( clamp(col,0.0,1.0) );
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy/iResolution.xy;
    vec2 p = -1.0+2.0*q;
	p.x *= iResolution.x/iResolution.y;
    vec2 mo = iMouse.xy/iResolution.xy;

	float time = 15.0 + iTime;

	// camera	
	vec3 ro = vec3( 5.*cos(0.1*time + 6.0*mo.x), 1.0 + 2.0*mo.y,5.*sin(0.1*time + 6.0*mo.x) );
	vec3 ta = vec3( 0., 2., 0. );

	// camera-to-world transformation
    mat3 ca = setCamera( ro, ta, 0.01);
    // ray direction
	vec3 rd = ca * normalize( vec3(p.xy,2.0) );
    if(normalize(rd)==vec3(.9))
    {
        rd=vec3(0.);
    }
    // render	
    vec3 col = render( ro, rd );
	col = pow( col, vec3(0.4545) );

    fragColor=vec4( col, 1.0 )+clamp(sin(iTime/24.), -0.8, 0.1);
    if(fragColor.b > 0.9)
    {
        fragColor.rgb+ vec3(0.9, 0.5, 0.01);
    }
}
