// Created by Alex Kluchikov

#define float3 vec3
#define float2 vec2
#define float4 vec4

float3 MIX(float3 x, float3 y)
{
	return abs(x-y);
}    

float CV(float3 c, float2 uv)
{
    float size=640./iResolution.x*0.003;
	return 1.0-clamp(size*(length(c.xy-uv)-c.z),0.,1.);
}

void mainImage(out float4 O, in float2 I)
{
    O=float4(0,0,0,1);
    for(float i=0.;i<60.;i+=4.5)
    {
        float3 c=float3(
            sin(i*0.57+7.+iTime*.7),sin(i*0.59-15.-iTime*.65),sin(i*0.6+iTime*.9)
            )*0.75+0.75;
		O.rgb=MIX(O.rgb,c*CV(float3(
			sin(iTime*0.5+i/4.5)*(iResolution.x/2.-60.)+iResolution.x/2.,
			sin(iTime*0.73+i/3.)*(iResolution.y/2.-60.)+iResolution.y/2.,
			0.0),I));
    }
}
