vec4 cubic(float x)
{
    float x2 = x * x;
    float x3 = x2 * x;
    vec4 w;
    w.x =   -x3 + 3.0*x2 - 3.0*x + 1.0;
    w.y =  3.0*x3 - 6.0*x2       + 4.0;
    w.z = -3.0*x3 + 3.0*x2 + 3.0*x + 1.0;
    w.w =  x3;
    return w / 6.0;
}

vec4 BicubicTexture(in sampler2D tex, in vec2 coord)
{
	vec2 resolution = iResolution.xy;

	coord *= resolution;

	float fx = fract(coord.x);
    float fy = fract(coord.y);
    coord.x -= fx;
    coord.y -= fy;

    fx -= 0.5;
    fy -= 0.5;

    vec4 xcubic = cubic(fx);
    vec4 ycubic = cubic(fy);

    vec4 c = vec4(coord.x - 0.5, coord.x + 1.5, coord.y - 0.5, coord.y + 1.5);
    vec4 s = vec4(xcubic.x + xcubic.y, xcubic.z + xcubic.w, ycubic.x + ycubic.y, ycubic.z + ycubic.w);
    vec4 offset = c + vec4(xcubic.y, xcubic.w, ycubic.y, ycubic.w) / s;

    vec4 sample0 = texture(tex, vec2(offset.x, offset.z) / resolution);
    vec4 sample1 = texture(tex, vec2(offset.y, offset.z) / resolution);
    vec4 sample2 = texture(tex, vec2(offset.x, offset.w) / resolution);
    vec4 sample3 = texture(tex, vec2(offset.y, offset.w) / resolution);

    float sx = s.x / (s.x + s.y);
    float sy = s.z / (s.z + s.w);

    return mix( mix(sample3, sample2, sx), mix(sample1, sample0, sx), sy);
}

vec3 ColorFetch(vec2 coord)
{
 	return texture(iChannel0, coord).rgb;   
}

vec3 BloomFetch(vec2 coord)
{
 	return BicubicTexture(iChannel3, coord).rgb;   
}

vec3 Grab(vec2 coord, const float octave, const vec2 offset)
{
 	float scale = exp2(octave);

    coord /= scale;
    coord -= offset;

    return BloomFetch(coord);
}

vec2 CalcOffset(float octave)
{
    vec2 offset = vec2(0.0);

    vec2 padding = vec2(10.0) / iResolution.xy;

    offset.x = -min(1.0, floor(octave / 3.0)) * (0.25 + padding.x);

    offset.y = -(1.0 - (1.0 / exp2(octave))) - padding.y * octave;

	offset.y += min(1.0, floor(octave / 3.0)) * 0.35;

 	return offset;   
}

vec3 GetBloom(vec2 coord)
{
 	vec3 bloom = vec3(0.0);

    //Reconstruct bloom from multiple blurred images
    bloom += Grab(coord, 1.0, vec2(CalcOffset(0.0))) * 0.01;
    bloom += Grab(coord, 2.0, vec2(CalcOffset(1.0))) * 0.02;
	bloom += Grab(coord, 3.0, vec2(CalcOffset(2.0))) * 0.08;
    bloom += Grab(coord, 4.0, vec2(CalcOffset(3.0))) * 0.16;
    bloom += Grab(coord, 5.0, vec2(CalcOffset(4.0))) * 0.32;
    bloom += Grab(coord, 6.0, vec2(CalcOffset(5.0))) * 0.64;
    bloom += Grab(coord, 7.0, vec2(CalcOffset(6.0))) * 1.28;
    bloom += Grab(coord, 8.0, vec2(CalcOffset(7.0))) * 2.56;

	return bloom;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 color = ColorFetch(uv);

    color += GetBloom(uv) * 0.1;
    color *= 20.0;

    color = color / (1.0 + color); //Tonemapping

    fragColor = vec4(color, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float speed = iTime * 0.666;
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = uv * 2. - 1.;
    p.x *= iResolution.x / iResolution.y;
    fragColor = vec4(0.0);
	for (int i = 0; i < 32; ++i) { //draw flares     

		float intensity = smoothstep(0.99,1.,saturate(sin(speed + float(i))) ); // sequential flare intensity animation
        intensity += smoothstep(0.994,1.,saturate(sin(speed + float(i) + 0.3)) ) * 0.1;

        if (intensity > 0.0) //don't draw if intensity is zero
        {
            vec2 randomoffset = hash21(float(i)*3.21); //random pos offset
            vec2 prot = rotateUV(p + randomoffset * vec2(iResolution.x / iResolution.y, 1.), (speed * 0.5)); //apply offset & rotate uvs
            //prot = fract( (prot*0.5+0.5) + vec2(iTime*0.2,0.) ) * 2. - 1.; //kaleidascope effect
            float rnd = float(i)*1.337; //per flare random val
            fragColor.xyz += drawflare(prot, intensity, rnd, speed, i); //draw
        }

    }   
    //fragColor += texture(iChannel0, uv + vec2(sin(iTime*3.)*0.005)) * (sin(iTime)+1.) * 0.5; //feedback effect wip
}

vec3 rgb2hsv(vec3 c)
{
    vec4 k = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, k.wz), vec4(c.gb, k.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 k = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + k.xyz) * 6.0 - k.www);
    return c.z * mix(k.xxx, clamp(p - k.xxx, 0.0, 1.0), c.y);
}

float saturate(float x)
{
	return clamp(x, 0.,1.);
}

vec2 hash21(float p) //1 in 2 out hash function
{
	vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
	p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.xx+p3.yz)*p3.zy) * 2. - 1.;

}

vec2 rotateUV(vec2 uv, float rotation)
{
    return vec2(
        cos(rotation) * uv.x + sin(rotation) * uv.y,
        cos(rotation) * uv.y - sin(rotation) * uv.x
    );
}

vec3 drawflare(vec2 p, float intensity, float rnd, float speed, int id)
{
    float flarehueoffset = (1. / 32.) * float(id) * 0.1;
    float lingrad = distance(vec2(0.), p); //linear radial gradient
    float expgrad = 1. / exp(lingrad * (fract(rnd) * 0.66 + 0.33)); //exponential radial gradient
    vec3 colgrad = hsv2rgb(vec3( fract( (expgrad * 8.) + (speed) + flarehueoffset), pow(1.-abs(expgrad*2.-1.), 0.45), 20.0 * expgrad * intensity)); //rainbow spectrum effect
    float blades = length(p * sin(3.0 * atan(p.x, p.y))); //draw 6 blades
    float comp = pow(1.-saturate(blades), 12.); // sharpen effect
    comp += saturate(expgrad-0.9) * 3.;
    comp = pow(comp * expgrad, 8. + (1.-intensity) * 5.); // compose and sharpen effect
    return vec3(comp) * colgrad; // apply color
}

float dist(vec3 a, vec3 b) { return abs(a.x - b.x) + abs(a.y - b.y) + abs(a.z - b.z); }

vec3 saturate(vec3 x)
{
    return clamp(x, vec3(0.0), vec3(1.0));
}

//First bloom pass, mipmap tree thing

vec3 ColorFetch(vec2 coord)
{
 	return texture(iChannel0, coord).rgb;   
}

vec3 Grab1(vec2 coord, const float octave, const vec2 offset)
{
 	float scale = exp2(octave);

    coord += offset;
    coord *= scale;

   	if (coord.x < 0.0 || coord.x > 1.0 || coord.y < 0.0 || coord.y > 1.0)
    {
     	return vec3(0.0);   
    }

    vec3 color = ColorFetch(coord);

    return color;
}

vec3 Grab4(vec2 coord, const float octave, const vec2 offset)
{
 	float scale = exp2(octave);

    coord += offset;
    coord *= scale;

   	if (coord.x < 0.0 || coord.x > 1.0 || coord.y < 0.0 || coord.y > 1.0)
    {
     	return vec3(0.0);   
    }

    vec3 color = vec3(0.0);
    float weights = 0.0;

    const int oversampling = 4;

    for (int i = 0; i < oversampling; i++)
    {    	    
        for (int j = 0; j < oversampling; j++)
        {
			vec2 off = (vec2(i, j) / iResolution.xy + vec2(0.0) / iResolution.xy) * scale / float(oversampling);
            color += ColorFetch(coord + off);


            weights += 1.0;
        }
    }

    color /= weights;

    return color;
}

