float pi = 3.14159265359;

//-----------------------------------------------------------------
// Digit drawing function by P_Malin (https://www.shadertoy.com/view/4sf3RN)

float SampleDigit(const in float n, const in vec2 vUV)
{		
	if(vUV.x  < 0.0) return 0.0;
	if(vUV.y  < 0.0) return 0.0;
	if(vUV.x >= 1.0) return 0.0;
	if(vUV.y >= 1.0) return 0.0;

	float data = 0.0;

	     if(n < 0.5) data = 7.0 + 5.0*16.0 + 5.0*256.0 + 5.0*4096.0 + 7.0*65536.0;
	else if(n < 1.5) data = 2.0 + 2.0*16.0 + 2.0*256.0 + 2.0*4096.0 + 2.0*65536.0;
	else if(n < 2.5) data = 7.0 + 1.0*16.0 + 7.0*256.0 + 4.0*4096.0 + 7.0*65536.0;
	else if(n < 3.5) data = 7.0 + 4.0*16.0 + 7.0*256.0 + 4.0*4096.0 + 7.0*65536.0;
	else if(n < 4.5) data = 4.0 + 7.0*16.0 + 5.0*256.0 + 1.0*4096.0 + 1.0*65536.0;
	else if(n < 5.5) data = 7.0 + 4.0*16.0 + 7.0*256.0 + 1.0*4096.0 + 7.0*65536.0;
	else if(n < 6.5) data = 7.0 + 5.0*16.0 + 7.0*256.0 + 1.0*4096.0 + 7.0*65536.0;
	else if(n < 7.5) data = 4.0 + 4.0*16.0 + 4.0*256.0 + 4.0*4096.0 + 7.0*65536.0;
	else if(n < 8.5) data = 7.0 + 5.0*16.0 + 7.0*256.0 + 5.0*4096.0 + 7.0*65536.0;
	else if(n < 9.5) data = 7.0 + 4.0*16.0 + 7.0*256.0 + 5.0*4096.0 + 7.0*65536.0;

	vec2 vPixel = floor(vUV * vec2(4.0, 5.0));
	float fIndex = vPixel.x + (vPixel.y * 4.0);

	return mod(floor(data / pow(2.0, fIndex)), 2.0);
}

float PrintInt(const in vec2 uv, const in float value )
{
	float res = 0.0;
	float maxDigits = 1.0+ceil(log2(value)/log2(10.0));
	float digitID = floor(uv.x);
	if( digitID>0.0 && digitID<maxDigits )
	{
        float digitVa = mod( floor( value/pow(10.0,maxDigits-1.0-digitID) ), 10.0 );
        res = SampleDigit( digitVa, vec2(fract(uv.x), uv.y) );
	}

	return res;	
}

vec4 circle(vec2 p, vec2 center, float radius)
{
	return mix(vec4(1,1,1,0), vec4(1,0,0,1), smoothstep(radius + 0.005, radius - 0.005, length(p - center)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 xy = uv;
    vec2 dDim = vec2(120,68);  
    vec2 PDxy = vec2(10.0,30.0)/dDim;
    float m_fps = 2.0;
    float d0 = round(fract(float(iFrame)/2.0/m_fps)+0.001);
    float d1 = round(fract(float(iFrame-1)/2.0/m_fps)+0.001);
    float v = 2.0;  
    float p = 0.02;  
    float w = 0.9;
    float mag = 0.5;
    vec2 parity = -sign(uv.x-0.5)*vec2(1.0,0.0);	
    vec2 dxdy = iMouse.xy/iResolution.xy-0.5;
    float verg = -0.01+dxdy.x;    

    if(true)
        uv.x = fract(uv.x*2.0);		
      	dDim = dDim*vec2(0.5,1.0);	
    	p *= 2.0;

    uv.x += parity.x*verg;
    uv -= 0.5;

    if(sin(2.0*pi/p*uv.x) > w)
        fragColor = vec4(1,1,1,1);
	else
		fragColor = vec4(0,0,0,1);

    float r = 0.1*mag;
    float s = 1.0;
    float l = .02;
    float d = -0.6;
    float vOffset = -0.15*mag;

    if(circle(uv,vec2(0.0,-vOffset),r).x>0.5 && circle(uv,vec2(0.0,-vOffset),r).y<0.5){
        if ( ( (cos(2.0*pi/l*(uv.x )) > d ) ) && ( abs( uv.y + vOffset) < .005) ){       
            fragColor = 0.8*vec4(1,1,0,1);
        }else{
        	fragColor = vec4(1,0,0,1);
        }
    }

    float pos = s*fract(iTime*v/s)-0.5;
    if(circle(uv,vec2(pos,vOffset),r).x>0.5 && circle(uv,vec2(pos,vOffset),r).y<0.5){
        if ( ( (cos(2.0*pi/l*(uv.x - pos)) > d ) ) && ( abs( uv.y - vOffset ) < .005) ){       
            fragColor = 0.8*vec4(1,1,0,1);
        }else{
        	fragColor = vec4(1,0,0,1);
        }
    }

    uv += 0.5;
    uv.x -= parity.x*verg;
    if((1.0-uv.x)<PDxy.x && (uv.x)>0.0 && (uv.y)<PDxy.y && (uv.y)>0.0 && xy.x >0.5){		
        fragColor = vec4(d0,d0,d0,1.0);
    }
    /*if((uv.x)<2.0*PDxy.x && (uv.x)>PDxy.x && (uv.y)<PDxy.y && (uv.y)>0.0){
        fragColor = vec4(d1,d1,d1,1.0);
    }*/


    float col = PrintInt( (uv-vec2(0.01,0.85))*20.0, iFrameRate );
    fragColor += vec4(col,col,col,1.0);
}
