// spec: https://www.khronos.org/registry/OpenGL-Refpages/gl4/html/clamp.xhtml

#define myclamp(x, minVal, maxVal)  min(max(x, minVal), maxVal)
//#define myclamp(x, minVal, maxVal) max(min(x, maxVal), minVal)

void mainImage( out vec4 O, vec2 u )
{
	vec2 U = u / iResolution.xy; 
    float i = 2.*U.x, j = 2.*U.y,
          x = 3.*fract(2.*U.x)-1.;

    if ( abs(j-1.)<.01 || abs(i-1.) < .005 ) { O = vec4(sin(u.x+u.y)); return; }

    O = vec4( j>1. ?
                 i<1. ? clamp(x,0.,1.)
               :        clamp(0.,1.,x)
             :   i<1. ? myclamp(x,0.,1.)
               :        myclamp(0.,1.,x)
             );

    if (O.x==0.) O = vec4(0,0,1,0);
    if (O.x==1.) O = vec4(1,0,0,0);
}
