// Thanks to coyote, fb39ca4, P_Malin and Stubbe for the awesome ideas
#define V vec3(
#define N normalize(V

void mainImage( out vec4 Y, vec2 X )
{
	vec3 	v,
        	//v = iResolution,
    		u = V+1, 0, 0 ),
    		n = u,
    		p = V+2, .851, -2.87677 ),
       	 	c = N.2 * u + p ) ),
        	d = N+cross( c, u ) ) );

    // setup ray
	v = d = mat3( d, cross( d, c ), c ) * N+960, 540, 1080 ) - V+X, 0 ) );
    //v = d = mat3( d, cross( d, c ), c ) * N.5 * v.xy - X, v.y ) );

    // raymarch scene and shadows
    for ( int i = 0; i < 128; ++i )
        // nearest sphere
        c = clamp( ceil( p - .5 ), -2., 2. ),
		c.x = .4 - .3 * fract( Y.w = c.y * 2.5 + c.z * .5 ),

        p -= i == 63 ?
                d = mod( p, 2. ) - 1.,
                u = p.x > .1 ?
                        // sphere color
    					n = N+p - c ) ),
                        ceil( Y.w + 5.5 ) * V-8, 3, 6 ) * .02 + u + u
                        :            
                        // plane normal and color
                        V.0 < d.y * d.z ? .8 : texture( iChannel0, p.yz * .1 ).x )
                            * smoothstep( 0., .5, length( p - sign( c - c * n ) ) ), 

                // raymarch shadows                  
                d = N-7, 6, 5 ) ),
				d * .01
				: 
    			d * min( p, length( p - c ) - c ).x;


    // compute lighting
    Y.xyz = sqrt( dot( -n, d ) * V.9 < p.x ) * 
        		(
            		u * V.6, .51, .33 ) + pow( max( dot( reflect( v, n ), d ), 0. ), 16. )
        		)
        + ++n.x * u * V.5, .7, 1 ) / 8.
    );
}
