#define rnd(p) fract( sin( p * vec2(17.1,311.7) ) *43758.5453123 )

void mainImage( out vec4 O, vec2 U )
{
    U -= .5;
    vec2 R = iResolution.xy;
    float p = 64./R.y;
    U *= p;
    int y = int( U.y * (1.+iMouse.y/R.y) )%4;

    O = iMouse.z>0. ? vec4(0)
          : mat4(.7,0,1,0, .35,1,.1,0,  1,.55,0,0, .2,.4,1,0 ) [y]; // background

    for ( float i=1.; i<20.; i++ )  {                               // spots loop
        if ( ((1<<y) & int[](5,10,6,9)[int(4.*rnd(7.7*i).x)] ) !=0 )// select lines
            O = mix( O, vec4(1,.86,.7,0),                           // draw spot
            //  smoothstep( p, 0., length(  U - p*R*rnd(i) ) - 6.5 ) );
                smoothstep( p, 0., length( (fract((U-3.*iTime)/p/R - rnd(i))-.5)*p*R ) -6.5 ) );
    }
}
