void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 bar = vec2(iMouse.x / iResolution.x, iMouse.y / iResolution.y);

    {
        if(uv.x >= bar.x - 0.01 && uv.x <= bar.x + 0.01)
            fragColor = vec4(0.0);
        else fragColor = vec4(1.0);
    }

    {
       if(uv.y >= bar.y - 0.015 && uv.y <= bar.y + 0.015)
            fragColor = vec4(0.0);
    }


}
