/*by musk License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License. */

#define load (fract(iTime*.1+sin(iTime*4.0)*.01))


#define t (load*2.0-51.2)
vec4 circles(vec2 p,float s) {
   p=p*s;
   vec2 pr = fract(p+vec2(.5))-vec2(.5);
   vec2 i = p-pr; p=pr;
   p+=(sin(dot(i,vec2(81.2,54.24))+t)+sin(dot(i,vec2(1.2,5.24))+t))*.13;
   float z=sin(dot(i,vec2(51.154,5368.1)))*.01;
   vec3 c = vec3(sin(i.x+i.y*41.2)*.5+.5,cos(i.y)*.5+.5,sin(i.x*2.4)*.5+.5);
   c=mix(c,vec3(1.9,.4,.1),.5);
   float sh = -(sqrt(dot(p,p))-.2)*iResolution.y/(.4+s*s*s*s);
   sh=max(0.0,min(1.0,sh));
   sh=sin(sh);
   return vec4(c,sh/s);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 uvp = fragCoord.xy / iResolution.yy-vec2(.5);

    vec3 w = vec3(.0);
    for (int i=1;i<20;i++){
        float fi = float(i);
        fi=fi*.2+.05;
        vec4 c=circles(uvp+vec2(fi*3.725+t*.1,t*-0.2/fi)+vec2(cos(fi),sin(fi))*t*.01,fi);
        w+=c.rgb*c.a*.25;
    }

    if(uv.y<.31&&uv.y>.30&&uv.x<load)w+=vec3(1.0);
    w+=vec3(.4,.5,.7)*.1*uv.y;
    w/=(1.0+pow(length(uv+vec2(-load,-.3))*4.0,2.0));
    float g=length(w);
    w=mix(w,vec3(g),g*.2-.3)*.7,
    w=pow(w,vec3(1.0/1.8));
	fragColor = vec4(w,1.0);
}
