// variant of https://shadertoy.com/view/4tSfDD
// trying to reproduce the coloring scheme of https://www.youtube.com/watch?v=r5GbvJ-LKH8

#define SCALE 2

// some descriptions and videos:
//     http://dmishin.blogspot.com/2013/11/the-single-rotation-rule-remarkably.html
//     https://dmishin.github.io/
//     https://www.youtube.com/watch?v=r5GbvJ-LKH8
//     https://www.youtube.com/watch?v=lkdnOuzHdFE 

void mainImage( out vec4 O, vec2 U ) {
    O = texelFetch(iChannel0,ivec2(U)/SCALE,0);
   // O += .3*texelFetch(iChannel1,ivec2(U)/SCALE,0);

    if (iMouse.z>0. && length(U-iMouse.xy)/iResolution.y < .2 ) 
        O = texelFetch(iChannel1,ivec2(U)/SCALE,0).xxxx;
}

#define SCALE 2.

#define T(i,j) texelFetch(iChannel0,ivec2(mod(U+vec2(i,j),floor(iResolution.xy/SCALE/2.)*2.)),0)

void mainImage( out vec4 O, vec2 U )
{
    if (iFrame==0) {
      //float l = sin(100.* length(U/iResolution.xy - 1./2./SCALE) )-.8; 
        float l = 1. - 3.5*SCALE*length(U/iResolution.xy - 1./2./SCALE); 
        O = vec4(fract(4321.*sin(1234.7*(U.x+17.31*U.y))) < l ); // init state 
        return;
    }

	O = T(0,0); 
    O.yzw = O.xyz;                               // compute in x, history in yzw

    //for(int i=0; i<4000000; i++) O += 0.;      // slow down

    vec2   s = vec2(iFrame%2);
         //s = vec2(0);                          // alterning quads ( Margolus neighborhood )
    vec2 V = mod(U-.5-s,2.); U -= V;             // location in current quad
    if ( (T(0,0)+T(1,0)+T(0,1)+T(1,1)).x == 1. ) // change only if one single point in quad   
          O.x = V==vec2(1,0) ? T(0,0).x
              : V==vec2(1,1) ? T(1,0).x
              : V==vec2(0,1) ? T(1,1).x
              :                T(0,1).x;

  //if ( iMouse.z > 0. && U+V-.5 == floor(iMouse.xy/SCALE) ) O = vec4(1); // mouse draw
}

// coloring scheme inspired from https://www.youtube.com/watch?v=r5GbvJ-LKH8

void mainImage( out vec4 O, vec2 U ) {
    O = texelFetch(iChannel1,ivec2(U),0);
    O =   O==vec4(0)                           ? vec4(0,0,0,0) 
        : O==vec4(1)                           ? vec4(1,0,0,0) // still    : red
        : O==vec4(1,0,1,0) || O==vec4(0,1,0,1) ? vec4(0,1,0,0) // period 2 : green
        : O.x+O.y+O.z+O.a==1.                  ? vec4(0,0,1,0) // period 4 : blue
        : O;                                                   // others

    if (iFrame>0) O = O*.05 + .99*texelFetch(iChannel0,ivec2(U),0);     // time blur
}
