// Tuned via XShade (http://www.funparadigm.com/xshade/)

float dstepf = 0.0;

const vec3 ld = vec3(0.,1., .5);

float t = 0.;

vec2 m;

float fullAtan(vec2 p)
{
    return step(0.0,-p.x)*3.14159 + sign(p.x) * atan(p.x, sign(p.x) * p.y);
}

float shape(vec2 p, float a)
{
    vec2 q = p;

    float aa = iTime*2.;
    p *= mat2(cos(aa),-sin(aa),sin(aa),cos(aa));

    float df0 = length(p) + 1.;				// circle
    float df1 = max(abs(p.x)-p.y,p.y) - 0.5;// triangle

    float st = 3.14159 * .1; // fraction of pi for avoid breack in df

    float am = mod(a, st) / st; // repeat by st => range 0. to 1.

    float d = mix(
        mix(df1,df0,am*0.5), // start df1, middle df0 (am => 0 to 0.5)
        mix(df0,df1, am*0.5+0.5),// middle df0, end df1, (am => 0.5 to 1.)
        am); // am => 0. to 1.

    return d;
}

vec2 df(vec3 p)
{
	float a = fullAtan(p.xz)*floor(m.x*10.)+iTime;

    float path = length(p.xz) - 2.5;
    vec2 rev = vec2(path, p.y - 2.5);

   	rev *= mat2(cos(a),-sin(a),sin(a),cos(a));

	vec2 res = vec2(abs(shape(rev, a))-0.032, 0);
    if (iMouse.z > 0.)
		if (p.z+m.y*10.-5. > res.x) 
            res = vec2(p.z+m.y*10.-5., 1);

    dstepf += 0.0005;

	return res; 
}


vec3 nor( in vec3 pos, float prec )
{
	vec3 eps = vec3( prec, 0., 0. );
	vec3 nor = vec3(
	    df(pos+eps.xyy).x - df(pos-eps.xyy).x,
	    df(pos+eps.yxy).x - df(pos-eps.yxy).x,
	    df(pos+eps.yyx).x - df(pos-eps.yyx).x );
	return normalize(nor);
}

// return color from temperature 
//http://www.physics.sfasu.edu/astro/color/blackbody.html
//http://www.vendian.org/mncharity/dir3/blackbody/
//http://www.vendian.org/mncharity/dir3/blackbody/UnstableURLs/bbr_color.html
vec3 blackbody(float Temp)
{
	vec3 col = vec3(255.);
    col.x = 56100000. * pow(Temp,(-3. / 2.)) + 148.;
   	col.y = 100.04 * log(Temp) - 623.6;
   	if (Temp > 6500.) col.y = 35200000. * pow(Temp,(-3. / 2.)) + 184.;
   	col.z = 194.18 * log(Temp) - 1448.6;
   	col = clamp(col, 0., 255.)/255.;
    if (Temp < 1000.) col *= Temp/1000.;
   	return col;
}

// get density of the df at surfPoint
// ratio between constant step and df value
float SubDensity(vec3 surfPoint, float prec, float ms) 
{
	vec3 n;
	float s = 0.;
    const int iter = 10;
	for (int i=0;i<iter;i++)
	{
		n = nor(surfPoint,prec); 
		surfPoint = surfPoint - n * ms; 
		s += df(surfPoint).x;
	}

	return 1.-s/(ms*float(iter)); // s < 0. => inside df
}

float SubDensity(vec3 p, float s) 
{
	vec3 n = nor(p,s); 							// precise normale at surf point
	return df(p - n * s).x;						// ratio between df step and constant step
}

// from shane sahders
// Tri-Planar blending function. Based on an old Nvidia writeup:
// GPU Gems 3 - Ryan Geiss: http://http.developer.nvidia.com/GPUGems3/gpugems3_ch01.html
vec3 tex3D( sampler2D tex, in vec3 p, in vec3 n ){

    n = max((abs(n) - .2)*7., .001);
    n /= (n.x + n.y + n.z );  

	p = (texture(tex, p.yz)*n.x + texture(tex, p.zx)*n.y + texture(tex, p.xy)*n.z).xyz;

    return p*p;
}

// from shane sahders
// Texture bump mapping. Four tri-planar lookups, or 12 texture lookups in total. I tried to 
// make it as concise as possible. Whether that translates to speed, or not, I couldn't say.
vec3 doBumpMap( sampler2D tx, in vec3 p, in vec3 n, float bf){

    const vec2 e = vec2(0.001, 0);

    // Three gradient vectors rolled into a matrix, constructed with offset greyscale texture values.    
    mat3 m = mat3( tex3D(tx, p - e.xyy, n), tex3D(tx, p - e.yxy, n), tex3D(tx, p - e.yyx, n));

    vec3 g = vec3(0.299, 0.587, 0.114)*m; // Converting to greyscale.
    g = (g - dot(tex3D(tx,  p , n), vec3(0.299, 0.587, 0.114)) )/e.x; g -= n*dot(n, g);

    return normalize( n + g*bf ); // Bumped normal. "bf" - bump factor.

}

// from iq code
float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i<1; i++ )
    {
		float h = df( ro + rd*t ).x;
        res = min( res, 8.0*h/t );
        t += h*.25;
        if( h<0.001 || t>tmax ) break;
    }
    return clamp( res, 0., 1. );
}

// from iq code
float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<10; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = df( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );    
}

//--------------------------------------------------------------------------
// Grab all sky information for a given ray from camera
// from Dave Hoskins // https://www.shadertoy.com/view/Xsf3zX
vec3 GetSky(in vec3 rd, in vec3 sunDir, in vec3 sunCol)
{
	float sunAmount = max( dot( rd, sunDir), 0.0 );
	float v = pow(1.0-max(rd.y,0.0),6.);
	vec3  sky = mix(vec3(.1, .2, .3), vec3(.32, .32, .32), v);
	sky = sky + sunCol * sunAmount * sunAmount * .25;
	sky = sky + sunCol * min(pow(sunAmount, 800.0)*1.5, .3);
	return clamp(sky, 0.0, 1.0);
}

vec4 shade(vec3 ro, vec3 rd, float d, vec3 lp, float li)
{
	vec3 p = ro + rd * d;											// surface point
	float sb = SubDensity(p, 0.01, 0.2);							// deep subdensity (10 iterations)
	vec3 bb = blackbody(80.*sb+30.);								// bb
	vec3 ld = normalize(lp-p); 										// light dir
	vec3 n = nor(p, 0.1);											// normal at surface point
	n = doBumpMap(iChannel0, p, n, 0.019);
	vec3 refl = reflect(rd,n);										// reflected ray dir at surf point 
	float amb = 0.23; 												// ambiance factor
	float diff = clamp( dot( n, ld ), 0.0, 1.0 ); 					// diffuse
	float fre = pow( clamp( 1. + dot(n,rd),0.0,1.0), 4. ); 			// fresnel
	float spe = pow(clamp( dot( refl, ld ), 0.0, 1.0 ),16.);		// specular
	float sss = 1. - SubDensity(p, 5.); 							// one step sub density of df
	return vec4(
        (diff + fre + bb.x * sss) * amb * li + spe, 
        (diff + fre + bb * sb * 0.8 + sss * 0.4) * amb * li + spe * 0.6 	
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    m = iMouse.xy/iResolution.xy;
    if (m.x == 0.) m.x = .1;

	fragColor = vec4(1);

	vec2 g = fragCoord.xy;
	vec2 si = iResolution.xy;
	vec2 uv = (2.*g-si)/min(si.x, si.y);

	vec2 camp = vec2(.5,.4) * 5.;
	vec3 rayOrg = vec3(cos(1.57) * camp.x * 2.,9.,sin(1.57) * camp.x * 2.);
	vec3 camUp = vec3(0,1,0);
	vec3 camOrg = vec3(0,1.2,0);

	float fov = .5;
	vec3 axisZ = normalize(camOrg - rayOrg);
	vec3 axisX = normalize(cross(camUp, axisZ));
	vec3 axisY = normalize(cross(axisZ, axisX));
	vec3 rayDir = normalize(axisZ + fov * uv.x * axisX + fov * uv.y * axisY);

	float s = 0.;
	float d = 0.;
	vec3 p = rayOrg + rayDir * d;
	float dMax = 20.;

	for (float i=0.; i<250.; i++)
	{
		if (log(d*d/s/1e6)>0. || d>dMax) break;
		s = df(p).x;
		d += s * 0.15;
		p = rayOrg + rayDir * d;	
	}

    vec3 sky = GetSky(rayDir, ld, vec3(1.5));

	if (d<dMax)
	{        
        vec4 sh = shade(rayOrg, rayDir, d, rayOrg, 1.5);

        fragColor.rgb = mix(sh.yzw, vec3(1.0,0.2,0.2) * sh.x, 1.0-exp(-d*dstepf));
	}
	else
	{
		fragColor = texture(iChannel1, rayDir);
	}
}
