const float pi = 3.1415926536;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float s=iTime*2.1;
    float e=floor(s*0.25);
    float d=fract(sin(e*7584.0)+5463.13);

    float focus = 256.0+4096.0*d;;
	vec3 eye = vec3(0.0,0.0,-focus);
    vec3 ray = vec3((fragCoord.xy-iResolution.xy*0.5)*2.0,-eye.z);

    float ax = e*pi-sin(s*.5)*1.2;
    float ay = e*pi+sin(s*d)*pi/6.0;

    vec3 pn = vec3(0.0,-sin(ax),cos(ax));

    vec3 p = eye-ray*dot(eye,pn)/dot(ray,pn);

	p = vec3(p.x*cos(ay)-p.y*sin(ay),p.x*sin(ay)+p.y*cos(ay),p.z);

    float q=((1.0-d)*0.1+d*0.9);

    vec2 cs = vec2(q*256.0,q*256.0);
  	vec2 co = vec2(0.0,iTime*6.0);
    vec3 color = vec3(0.0);

    float size = (1.0-fract(s))*128.0*q;

    float col = (
        clamp(size-distance(
            mod(p.xy+co*cs.x,cs),cs.xy*0.5
        ),0.0,1.0)*clamp(focus/(p.z-eye.z),0.0,1.0)
    );

	fragColor = vec4(col)*min(1.0,iTime*0.25);
}

const float seed = 14.12;
const float pi = 3.1415926536;
const float pi2 = pi*2.0;

// hash stolen here: https://www.shadertoy.com/view/XlfGWN
#define MOD2 vec2(.27232,.17369)
float hash11(float p)
{
	vec2 p2 = fract(vec2(p) * MOD2);
    p2 += dot(p2.yx, p2.xy+19.19);
	return fract(p2.x * p2.y);
}

vec2 mainSound( in int samp,float time)
{
    float y=time*2.1;
	float p=hash11(floor(y/16.0));
	float q=fract(1.0-y);
    float m=sin(24.0*pow(q,24.0))*0.9;
    float s=fract(q*4.0);
    float r=hash11(seed+p+mod(floor(y*4.0),16.0));
    float d=floor(r*12.0);
    float e=1.0-pow(1.0-2.0*s,64.0);
    float f=440.0*pow(2.0,-2.0+d/12.0);
    float w=sin(time*f*0.6*pi2)*s*pi2*(0.7+0.1*cos(y*pi*0.125));
    float t=sin(time*f*2.0*pi2+w)*s*0.3*e*smoothstep(0.5,0.5,r);
	return vec2(m+t);
}
