#define BANDING 10.0
#define rot(a)  mat2( cos(a), sin(a),  -sin(a), cos(a) )


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 tc = uv;
    uv *= 2.0;
    uv -= 1.0;
    uv *= rot(iTime * 0.1 + length(uv * ( (uv.x - 1.0) * 2.0)));


    float n = texture(iChannel0, vec2( 0.01, uv.y * 0.4)).r;
    float b = BANDING;// * length(uv * 100.0);
    float val = round(fract(n) * b) / b;
    vec3 col = vec3(val);
    col.r *= 2.0 - uv.y;
    col.g *= 0.8 - uv.y;
    col.b *= 1.2 + uv.x;

    //vec2 pos = vec2( smoothstep( 0.0, 1.0, fract(iTime * 0.5) ), 0.5);

    // with noise
    //float p = texture(iChannel0, (tc * uv) * 0.05).r;
    //vec2 pos = vec2( sin(iTime * 0.5 + p) * 2.0, cos(iTime - p) * 10.0);

    float p = texture(iChannel0, (tc * uv) * 0.01).r;
    vec2 pos = vec2( sin(iTime * 0.5) * 2.0 + cos(iTime*0.5 + length(uv)) * 2.0, 
                    cos(iTime*0.4) * 2.0 + p);
    //vec3 circ = vec3(length((uv - pos) * 2.0));
    vec3 circ = vec3(max(-0.75, 1.0 - min (length(uv - pos), 2.5)));
    circ *= dot(uv, vec2(0.0, 1.0));
    col += circ;

	fragColor = vec4(col,1.0);
}
