#define T iTime
#define PI 3.141592653
#define TAU (PI*2.)

#define P 0.001  // Precision
#define D 20.    // Max distance
#define S 256    // Marching steps
#define R 2.     // Marching substeps
#define K 32.    // Shadow softness
#define I 3      // Reflection marches
#define A 7.     // AO steps

struct Hit {
	vec3 p;
	float t;
	float d;
	float s;
};

struct Ray {
	vec3 o;
	vec3 d;
}, _ray;

struct Cam {
	vec3 p;
	vec3 t;
};

vec2 _uv;
Cam _cam = Cam(vec3(0,0,1), vec3(0));

const int _numObjects = 6;
float _d, _obj[_numObjects], _bounce;

int _ignore = -1;


bool _ambientOccMarch = false;
bool _shadowMarch = false;
bool _normalMarch = false;

// https://iquilezles.org/articles/distfunctions
float sdEllipsoid(vec3 p,vec3 r){return (length(p/r)-1.)*min(min(r.x,r.y),r.z);}
float udBox(vec3 p,vec3 s,float r){return length(max(abs(p)-s+r,0.))-r;}
float sdBox(vec3 p,vec3 s){vec3 d = abs(p)-s;return min(max(d.x,max(d.y,d.z)),0.)+length(max(d,0.));}

mat2 rot(float a){float c=cos(a),s=sin(a);return mat2(s,c,-c,s);}

float smax( float a, float b, float k )
{
    float h = clamp( 0.5 + 0.5*(b-a)/k, 0.0, 1.0 );
    return mix( a, b, h ) + k*h*(1.0-h);
}

// blur the track to remove sharp edges
float track(vec3 p)
{
    vec2 c = vec2((p.x+.5),mod((atan(p.y,p.z)+PI)/TAU+T/226.316,1.));
    vec2 e = vec2(0.001,0);

    float f = textureLod(iChannel0,c,0.).r;;

    f += textureLod(iChannel0,c+e.xy,0.).r;
    f += textureLod(iChannel0,c-e.xy,0.).r;
    f += textureLod(iChannel0,c+e.yx,0.).r;
    f += textureLod(iChannel0,c-e.yx,0.).r;

    return f / 5.;
}

float screw(vec3 p)
{
	float d = sdEllipsoid(p,vec3(.05,.025,.05));
    d = max(d,-sdBox(p-vec3(0,+.06,0),vec3(.03,.05,.01)));
    d = max(d,-sdBox(p-vec3(0,+.06,0),vec3(.01,.05,.03)));

    return d;
}

float scene(vec3 p)
{
	vec3 q; float d = 1e10;

    float s = 1./36.;
    float w = .3;
    float l = .25;
    float r = .2;
    float t = track(p);

    // Floor
	_obj[0] = p.y+.2;

    // Tube
    _obj[1] = max(length(p.yz)-r,abs(p.x)-w-.04);
    _obj[1] -= t*.005;

    // Gaps
    q = p; q.x = mod(q.x,s)-s/2.;
    float f = floor((p.x-w)*36.+w)*.005;
    _obj[2] = udBox(q-vec3(0,0,-l-r-.01+f-.05),vec3(s/4.,.001,l+f),0.);

    // Teeth
    q = p; q.x = mod(q.x+s/2.,s)-s/2.;
    f = max(q.z+l-f*2.,0.);
    q.y -= f*.05*sin(T*100.)*t;
    _obj[2] = min(_obj[2],udBox(q-vec3(0,0,-l-r-.01),vec3(s/4.,.001,l),0.));

    // Boundary
    _obj[2] = max(_obj[2],udBox(p-vec3(0,0,-l-r-.01),vec3(w+s/2.,.1,l),0.));

    // Room boundary
    _obj[3] = abs(length(p)-18.)-1.;

    // Screws
    q = p; q.z += l*2.+r-.1; q.x -= w-.1;
    _obj[4] = screw(q);
    q = p; q.z += l*2.+r-.1; q.x += w-.1;
    _obj[4] = min(_obj[4],screw(q));

    // Box
    _obj[5] = sdBox(p-vec3(0,-.1,-l-.025),vec3(w+.1,.1,l*2.+.01))-.02;
    _obj[5] = smax(_obj[5],-sdBox(p-vec3(0,.1,-l-.025),vec3(w+.1,.2,l*2.+.01)-.05),.02);


    if (_normalMarch == true)
    {
        _obj[0] += textureLod(iChannel1,p.xz*.5,0.).r * .003;
    }

	for(int i = 0; i < _numObjects; i++)
	{
		if (_ignore == i) continue;
		d = min(d,_obj[i]);
	}

	_d = d;

	return d;
}

Ray lookAt(Cam c, vec2 uv)
{
	vec3 d = normalize(c.t - c.p);
	vec3 r = normalize(cross(d,vec3(0,1,0)));
	vec3 u = cross(r,d);

	return Ray(c.p, normalize(uv.x*r + uv.y*u + d));
}

Hit march(Ray r)
{
	float t = 0., d, s;
	vec3 p;

	for(int i = 0; i < S; i++)
	{
		d = scene(p = r.o + r.d*t);

		if (d < P || t > D)
		{
			s = float(i);
			break;
		}

		t += d/R;
	}

	return Hit(p, t, d, s);
}

vec3 getNormal(vec3 p)
{
    _normalMarch = true;

	vec2 e = vec2(P,0.);

	return normalize(vec3(
		scene(p+e.xyy)-scene(p-e.xyy),
		scene(p+e.yxy)-scene(p-e.yxy),
		scene(p+e.yyx)-scene(p-e.yyx)
	));
}

