float getAmbientOcclusion(Hit h) 
{
    _ambientOccMarch = true;

    float t = 0., a = 0.;

	for(float i = 0.; i < A; i++)
    {
        float d = scene(h.p-_ray.d*i/A*.2);
        t += d;
    }

	return clamp(t/A*20.,0.,1.);
}

vec3 getColor(Hit h)
{
	if (h.d > P) { return vec3(0); }

	vec3 col = vec3(0), c;
	vec3 n = getNormal(h.p);
    Hit _h = h;

    // this is supposed to simulate a flickering candle light
    vec3 light = vec3(0,5,0)+vec3(sin(T*5.),0,sin(T))*.02;

	float lastRef = 1.0;
	float ref = 1.0;

	for(int i = 0; i < I; i++)
	{
		float diff = max(dot(n, normalize(light-h.p)),.1);
		float spec = pow(max(dot(reflect(normalize(h.p-light),n),normalize(_cam.p-h.p)),0.),100.);
		float dist = 100./exp(length(h.p-light));

		if (_d == _obj[0])
		{
			c = textureLod(iChannel1,h.p.xz*.5,0.).rgb*dist*2.+spec*vec3(1,.7,.5);
		}
        // Tube
		else if (_d == _obj[1])
		{
			ref = .7;
			c = vec3(1,.9,.6)*diff+spec*5.;
		}
        // Teeth
		else if (_d == _obj[2])
		{
			ref = .7;
			c = vec3(1,.9,.6)*diff+spec*5.;
		}
        // Room Reflection
        else if (_d == _obj[3])
		{
			c = lastRef < 1. ? textureLod(iChannel2,n,0.).rgb : vec3(0);
		}
        // Screw heads
		else if (_d == _obj[4])
		{
			ref = .5;
			c = vec3(.9,.9,1)*diff*.5+spec*5.;
		}
        // Box
		else if (_d == _obj[5])
		{
			c = textureLod(iChannel1,h.p.xy*.5,0.).rgb*dist+spec*vec3(1,.7,0);
		}
        else { c = vec3(0); }

        if (h.d > P) { c = vec3(0); ref = 1.; }

		col = i == 0 ? c : mix(c,col,lastRef);

		if (ref < 1.0)
		{
            Ray r;
	       	r.d = normalize(reflect(h.p-_cam.p,n));
	        r.o = h.p+r.d*P*2.;

        	h = march(r);
			n = getNormal(h.p);

			lastRef = ref;
			ref = 1.0;
		}
		else { break; }
	}

	return col * getAmbientOcclusion(_h);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	_uv = (2.*fragCoord.xy-iResolution.xy)/iResolution.xx;

    vec2 uvc = (2.0*fragCoord.xy-iResolution.xy)/iResolution.xy;
    vec2 uvm = (2.0*iMouse.xy-iResolution.xy)/iResolution.yy;

    float t = max(T-2.,0.);

    if (t < 15.)
    {
        float f = pow(smoothstep(0.,1.,1.-min(t*.1,1.)),3.);

		uvm.y = 0.4;
        uvm.x = 0.2+.4*f;

        _cam.p.y = -2.*f;
        _cam.p.z = 2.*f+.5;
        _cam.t.z = -.2;
    }
    else if (t >= 15.)
    {
        if (iMouse.y < 10.) { uvm.y = 0.3; }
        if (iMouse.x < 10.) { uvm.x = 0.3+t*.01; }
    	_cam.p *= (1.+2.*pow(smoothstep(0.,1.,1.-min(T*.1,1.)),3.))*1.;
        _cam.t.z = -.3;
    }

    _cam.p.yz *= rot(-uvm.y*PI);
    _cam.p.xz *= rot(uvm.x*PI);

    _ray = lookAt(_cam,_uv);

    float f = 1.-length(uvc)*0.5;
	fragColor = vec4(getColor(march(_ray)),1);
}

