float df(vec2 uv)
{
	return max(abs(uv.x)*.866 - uv.y*.5,uv.y);
}

void mainImage( out vec4 fC, vec2 fG )
{
	vec2 p = fG /= iResolution.y / 10.;

    float d = 9.;

    for(int x=-3;x<=3;x++)
    for(int y=-3;y<=3;y++)
    {	
        p = vec2(x,y);
        vec2 a = sin( iTime + 9. * fract(sin((floor(fG)+p)*mat2(2,5,5,2))));
		p += .5 + .35*a - fract(fG);
        p *= mat2(cos(a.x),-sin(a.x),sin(a.x),cos(a.x)) * cos(a.y*1.5) * 5.;
        d = min(d, df(p));
    }

	fC = vec4(d);
}
