#ifndef PI
#define PI 3.1415926535897932384626433832795
#endif


vec2 rotate(vec2 v, float a) {
	float sinA = sin(a);
	float cosA = cos(a);
	return vec2(v.x * cosA - v.y * sinA, v.y * cosA + v.x * sinA); 	
}

float square(vec2 uv, float d) {
	return max(abs(uv.x), abs(uv.y)) - d;	
}

float smootheststep(float edge0, float edge1, float x)
{
    x = clamp((x - edge0)/(edge1 - edge0), 0.0, 1.0) * 3.14159265;
    return 0.5 - (cos(x) * 0.5);
}

float quadraticInOut(float t) {
  float p = 2.0 * t * t;
  return t < 0.5 ? p : -p + (4.0 * t) - 1.0;
}

float exponentialInOut(float t) {
  return t == 0.0 || t == 1.0
    ? t
    : t < 0.5
      ? +0.5 * pow(2.0, (20.0 * t) - 10.0)
      : -0.5 * pow(2.0, 10.0 - (t * 20.0)) + 1.0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
	uv = uv * 2.0 - 1.0;
	uv.x *= iResolution.x / iResolution.y;
	uv *= 1.5;

    float blurAmount = -0.005 * 1080.0 / iResolution.y;

	float period = 15.0;
	float time = iTime / period;
	time = mod(time, 1.0);
	time = smootheststep(0.0, 1.0, time);


	fragColor = vec4(0.0, 0.0, 0.0, 1.0);

	for (int i = 0; i < 25; i++) {
		float n = float(i);
		float size = 1.0 - n / 25.0;
		float rotateAmount = (n * 0.5 + 0.25) * PI * 2.0; 

        float animTime =  exponentialInOut((time / 2.0));

        float animTimeB =  quadraticInOut(time);

        if(n < 1.) {
            fragColor.rgb = mix(fragColor.rgb, vec3(0.467, 0.051, 1.), smoothstep(0.0, blurAmount, square(rotate(uv, -rotateAmount * animTime), size)));
