        }

		float blackOffset = mix(1.0 / 4.0, 1.0 / 2.0, n / 40.) / 40.0;


        fragColor.rgb = mix(fragColor.rgb, vec3(0.0, 0.0, 0.0), smoothstep(0.0, blurAmount, square(rotate(uv, -(rotateAmount + PI / 2.0) * animTimeB), size - blackOffset)));
    }
