const float PI = 3.1415927;

void mainImage(out vec4 O, vec2 I) {
    vec2 R = iResolution.xy;
    vec2 p = (2.0 * I - R) / R.y * 6.0;
    float t = 1.0 - abs(1.0 - mod(iTime / 2.0, 2.0));
    float a = (smoothstep(0.0, 1.0, smoothstep(0.0, 1.0, t)) - 0.5) * PI;
    float b = iMouse.z > 0.0 ? mix(0.2, 1.5, iMouse.x / R.x) : 0.6;
    float c = cos(a);
    float s = sin(a);
    vec3 o = vec3(0.0, 1.0, -1.0);
    mat2 A = mat2((c * o.yzyy + s * o.yyzy) * b + o.yzyy);
    mat2 B = inverse(A);
    vec2 P = B * p;
    vec4 Q = 2.0 * round((P.xyxy + o.xxyy) / 2.0) - o.xxyy;
    vec4 d = abs(vec4(A * Q.xy - p, A * Q.zw - p));
    mat2 C = mat2(c, -s, s, c);
    vec4 f = abs(vec4(C * (A * Q.xw - p), C * (A * Q.zy - p)));
    float g = b - min(max(f.x, f.y), max(f.z, f.w));
    float h = 1.0 - min(max(d.x, d.y), max(d.z, d.w));
    O = g >= 0.0
        ? vec4(1.000,0.302,0.533,1.0)
        : h >= 0.0
        ? vec4(0.204,0.490,0.957,1.0)
        : vec4(1.0);
    float i = min(abs(g), abs(h));
    O *= smoothstep(0.0, length(vec2(dFdx(i), dFdy(i))), i - 0.02);
}
