#define n_size_max_a_o_col_last_krnl const 1000;
float f_n_rand(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 o_trpino_nooffset = (fragCoord.xy)/ iResolution.xy;
    vec2 o_trmono_nooffset = (iMouse.xy)/ iResolution.xy;

    vec4 o_col_last = texture(iChannel0, o_trpino_nooffset);

    vec2 o_trpino = (fragCoord.xy - iResolution.xy*0.5)/ iResolution.y;
    vec2 o_trmono = (iMouse.xy - iResolution.xy*0.5)/ iResolution.y;
    if(iMouse.w == 0.0){
        float n_t = floor(iTime * 1.0);
        float n_t_x = fract(n_t/5.)-.5;
        float n_t_y = fract(n_t/10.)-.5;
        float n_speed = 10.;
        o_trmono = vec2(
            sin(iTime*n_speed),
            cos(iTime*(n_speed/5.))*0.5
            //n_t_x*2.,
            //n_t_y
            //(f_n_rand(n_t)-0.5)*2.,
            //f_n_rand(n_t*3.33)-0.5
        );
    }
    vec2 o_scale = vec2(0.9);
    vec2 o_fragCoord_scaled = ((fragCoord.xy)*o_scale);
    vec2 o_iResolution_scaled = (iResolution.xy * o_scale);

    vec2 o_fragCoord_scaled_floor = floor(o_fragCoord_scaled);
    vec2 o_iResolution_scaled_floor = floor(o_iResolution_scaled);
    vec2 o_fragCoord_scaled_fract = fract(o_fragCoord_scaled);
    vec2 o_iResolution_scaled_fract = fract(o_iResolution_scaled);

    vec2 o_trpino_scldfloor = (o_fragCoord_scaled_floor.xy - o_iResolution_scaled_floor.xy*0.5)/ o_iResolution_scaled_floor.y;

    vec2 o_scale_kernel = vec2(
        5.,
        5.
    );
    float n_size_kernel = o_scale_kernel.x * o_scale_kernel.y;
    //vec4[3] a_o_col_last_krnl = vec4[3];
    //vec3[32] palette = vec3[32];

    vec4 a_o_col_last_krnl[100];

    vec4 a_o_sum_xaxis[100];// 100 is static , because a varbiable as array size is not working 'array size must be a constant integer expression'...
    vec4 a_o_sum_xaxis_nor[100];// 100 is static , because a varbiable as array size is not working 'array size must be a constant integer expression'...
    vec4 a_o_sum_yaxis[100];// 100 is static , because a varbiable as array size is not working 'array size must be a constant integer expression'...
    vec4 a_o_sum_yaxis_nor[100];// 100 is static , because a varbiable as array size is not working 'array size must be a constant integer expression'...
    vec4 o_kernel_sum = vec4(0.0);
    int n_index_a_o_col_last_krnl = 0;
    for(float n_y = 0.0; n_y < o_scale_kernel.y; n_y+=1.){
        for(float n_x = 0.0; n_x < o_scale_kernel.x; n_x+=1.){
            int n_index_a_o_sum_xaxis = int(n_x);
            int n_index_a_o_sum_yaxis = int(n_y);

            vec2 o_speed = vec2(0.0);
            vec2 o = vec2(
               floor(n_x - floor(o_scale_kernel.x/2.)),
               floor(n_y - floor(o_scale_kernel.y/2.)) 
            ) + o_speed;
            vec2 o_off = vec2(
                o.x * (1./o_iResolution_scaled_floor.x),
                o.y * (1./o_iResolution_scaled_floor.y)
            );
            vec4 o_col_last_on_kernel = texture(iChannel0, o_trpino_nooffset+o_off);
            o_kernel_sum += o_col_last_on_kernel;

            a_o_sum_xaxis[n_index_a_o_sum_xaxis] += o_col_last_on_kernel;
            a_o_sum_xaxis_nor[n_index_a_o_sum_xaxis]+= (o_col_last_on_kernel / o_scale_kernel.x);
            a_o_sum_yaxis[n_index_a_o_sum_yaxis] += o_col_last_on_kernel;
            a_o_sum_yaxis_nor[n_index_a_o_sum_yaxis]+= (o_col_last_on_kernel / o_scale_kernel.y);

            n_index_a_o_col_last_krnl+=1;
            a_o_col_last_krnl[n_index_a_o_col_last_krnl] = o_col_last_on_kernel;
        }
    }
    vec4 o_kernel_sum_nor = o_kernel_sum / (o_scale_kernel.x*o_scale_kernel.y);


    vec4 a_o_sum_xring_nor[100];
    vec4 a_o_sum_yring_nor[100];
    for(float n_x =0.0; n_x < (o_scale_kernel.x/2.); n_x+=1.){
        vec4 n_sum1 = a_o_sum_xaxis_nor[int(n_x)];
        vec4 n_sum2 = a_o_sum_xaxis_nor[int(o_scale_kernel.x - n_x)];
        a_o_sum_xring_nor[int(n_x)] = (n_sum1 + n_sum2)/2.;
    };
    for(float n_y =0.0; n_y < (o_scale_kernel.y/2.); n_y+=1.){
        vec4 n_sum1 = a_o_sum_yaxis_nor[int(n_y)];
        vec4 n_sum2 = a_o_sum_yaxis_nor[int(o_scale_kernel.y - n_y)];
        a_o_sum_xring_nor[int(n_y)] = (n_sum1 + n_sum2)/2.;
    };
    //00000
    //01110
    //01.10
    //01110
    //00000 
    vec4 a_o_sum_xyring_nor[100];
    float n_scale_kernel_bigger = max(o_scale_kernel.x, o_scale_kernel.y);
    for(float n_index =0.0; n_index < n_scale_kernel_bigger; n_index+=1.){
        a_o_sum_xyring_nor[int(n_index)] = 
            (a_o_sum_xring_nor[int(n_index)] + 
            a_o_sum_yring_nor[int(n_index)]) /2.;
    }

    float n_index = floor(o_fragCoord_scaled_floor.y * o_iResolution_scaled_floor.x + o_fragCoord_scaled_floor.x);
    float n_index_nor = n_index / (o_iResolution_scaled_floor.x * o_iResolution_scaled_floor.y);

    if(iFrame == 0){
        fragColor = vec4((f_n_rand(float(n_index))));
    }else{
        if(mod(float(iFrame), 1.) > 1.){
            fragColor = o_col_last;
        }else{
            float n = 0.5;
            if(o_kernel_sum_nor.x > 0.59){
                fragColor = vec4(1.);
            }else{
                fragColor = vec4(0.);
            }

            //vec4 o_col_delta_last = abs(fragColor - o_col_last);
            //fragColor = o_col_delta_last*0.29;

        }

    }

    //float n_thresh = (sin(iTime*0.2)*0.5+0.5);
    float n_thresh = 0.20;
    //if(iMouse.w == 0.0){
    //    n_thresh = 1.-(float(mod(iTime, 20.)))*0.1;
    //}
    float n_dist = length(o_trpino-o_trmono);
    if(n_dist< n_thresh){
        float n_dist_nor = n_dist/n_thresh;
        //fragColor = vec4(sin(n_dist*6.2831)*0.5+0.5);
        //fragColor = mix(o_col_last, fragColor,n_dist);
        //fragColor = mix(o_col_last, fragColor,2.*n_dist_nor);
        //fragColor = mix(fragColor, vec4(f_n_rand(n_dist)), 0.9);
        //fragColor = vec4(f_n_rand(fragCoord.x*fragCoord.y));
        fragColor = vec4((n_dist_nor));

    }

    fragColor = (fragColor);

}
